import React, { Component } from "react";
import "./CreateProduct.css";
import axios from "axios";

export default class CreateProduct extends Component {
  state = {
    categories: {},
    selectedCategorie: "",
    selectedSubcategorie: "",
    product: {
      name: "",
      description: "",
      price: "",
      quantity: "",
      thumbnail: ""
    },
    attributes: {
      brand: "",
      color: "",
      weight: "",
      size: ""
    },
    imageUrl: "",
    imageArr: []
  };

  componentDidMount() {
    let url = `http://localhost:8086/catalogs/categories`;
    axios.get(url).then(res => {
      let categories = {};
      for (let i = 0; i < res.data.length; i++) {
        let categorie = res.data[i].name;
        categories[categorie] = [];
        for (let r = 0; r < res.data[i].subcategories.length; r++) {
          categories[categorie].push(res.data[i].subcategories[r].name);
        }
      }
      console.log(res.data[0].subcategories);
      
      this.setState({
        categories: categories,
        selectedCategorie: res.data[0].name,
        selectedSubcategorie: res.data[0]
      });
    });
  }
  resetFieldsHandler() {
    this.setState({
      product: {
        name: "",
        description: "",
        price: "",
        quantity: "",
        thumbnail: ""
      },
      attributes: {
        brand: "",
        color: "",
        weight: "",
        size: ""
      },
      imageUrl: "",
      imageArr: []
    });
  }

  render() {
    return (
      <div className="CreateProduct">
        <div className="product-wrapper">
          <div className="categories">
            <p className="label">Category</p>
            <select
              onChange={e =>
                this.setState({
                  selectedCategorie:
                    e.target.options[e.target.selectedIndex].text
                })
              }
            >
              {Object.keys(this.state.categories).map(categorie => (
                <option key={categorie} value={categorie}>
                  {categorie}
                </option>
              ))}
            </select>
          </div>
          <div className="subcategories">
            <p className="label">Subcategory</p>
            <select
            onChange={e =>
              this.setState({
                selectedSubcategorie:
                  e.target.options[e.target.selectedIndex].text
              })}>
              {this.state.categories[this.state.selectedCategorie] !== undefined
                ? this.state.categories[this.state.selectedCategorie].map(
                    subcategorie => (
                      <option key={subcategorie} value={subcategorie}>
                        {subcategorie}
                      </option>
                    )
                  )
                : null}
            </select>
          </div>
          <div className="description">
            <p className="label">Description:</p>
            <textarea type="text" placeholder="Description" />
          </div>
          <div className="thumbnail">
            <p className="label">Thumbnail URL:</p>
            <input type="text" placeholder="Thumbnail" />
          </div>
          <div className="name">
            <p className="label">Name:</p>
            <input type="text" placeholder="Name" />
          </div>
          <div className="price">
            <p className="label">Price:</p>
            <input type="text" placeholder="Price" />
          </div>
          <div className="attributes">
            <div className="color">
              <p className="label">Color:</p>
              <select>
                <option value="black">Black</option>
                <option value="gray">Gray</option>
                <option value="blue">Blue</option>
                <option value="green">Green</option>
                <option value="purple">Purple</option>
                <option value="pink">Pink</option>
                <option value="yellow">Yellow</option>
                <option value="orange">Orange</option>
                <option value="red">Red</option>
                <option value="brown">Brown</option>
                <option value="white">White</option>
                <option value="others">Others</option>
              </select>
            </div>
            <div className="brand">
              <p className="label">Brand:</p>
              <select>
                <option value="black">Black</option>
                <option value="gray">Gray</option>
              </select>
            </div>
            <div className="weight">
              <p className="label">Weight:</p>
              <select>
                <option value="black">Black</option>
                <option value="gray">Gray</option>
              </select>
            </div>
            <div className="size">
              <p className="label">Size:</p>
              <select>
                <option value="black">Black</option>
                <option value="gray">Gray</option>
              </select>
            </div>
          </div>
          <div className="images">
            <p className="label">Image Url:</p>
            <div className="image-wrapper">
              <input
                onChange={e => this.setState({ imageUrl: e.target.value })}
                value={this.state.imageUrl}
                type="text"
                placeholder="Image URL"
              />
              <i
                onClick={() => {
                  if (
                    this.state.imageArr.indexOf(this.state.imageUrl) === -1 &&
                    this.state.imageUrl !== ""
                  ) {
                    this.state.imageArr.push(this.state.imageUrl);
                    this.setState({ imageUrl: "" });
                  } else {
                    console.log("err");
                  }
                }}
                className="fas fa-plus"
              />
            </div>
          </div>
          <div className="image-list">
            {this.state.imageArr.map(url => (
              <div className="image-url" key={url}>
                {url}
                <i
                  className="fas fa-times"
                  onClick={() => {
                    let imageArr = this.state.imageArr;
                    imageArr.splice(imageArr.indexOf(url), 1);
                    this.setState({ imageArr: imageArr });
                  }}
                />
              </div>
            ))}
          </div>
          <div className="buttons">
            <button className="submit" onClick={() => console.log("submit")}>
              Submit
            </button>
            <button className="reset" onClick={() => this.resetFieldsHandler()}>
              Reset
            </button>
            <button
              className="cancel"
              onClick={() => this.props.history.push({ pathname: "/" })}
            >
              Cancel
            </button>
          </div>
        </div>
      </div>
    );
  }
}
