import React, { Component } from "react";
import "./ProductPage";
import Spinner from "../UI/Spinner/Spinner";
import axios from "axios";

export default class ProductPage extends Component {
  state = {
    product: {
      attributes: {},
      images: []
    },
    imgIndex: 0,
    loadData: false,
    showFullScreenImg: false
  };
  closeFullScreenHandler = () => {
    this.setState({ showFullScreenImg: false });
  };
  componentDidMount() {
    axios
      .get(
        `http://localhost:8086/catalogs/categories/audio/subcategories/headphones/products/${
          this.props.match.params.id
        }`
      )
      .then(res => {
        console.log(res.data);
        this.setState({
          product: res.data,
          attributes: res.data.attributes,
          images: res.data.images,
          loadData: true
        });
      });
  }
  render() {
    let product = <Spinner />;
    if (this.state.loadData) {
      product = (
        <React.Fragment>
          <div className="image-gallery">
            <div className="image-gallery-navigation">
              <div
                className="button-up-thumbnails"
                onClick={() => {
                  return (document.getElementById(
                    "scroll-thumbnails"
                  ).scrollTop =
                    document.getElementById("scroll-thumbnails").scrollTop -
                    100);
                }}
              />
              left
            </div>
          </div>


          <div className="image-gallery-thumbnails" id="scroll-thumbnails">
          {this.state.product.images.map((img, index) => (
            <div              
              key={index}
              className="center-img center-img-thumbnail"
              onClick={() => {
                this.setState({
                  ...this.state.product,
                  imgIndex: index
                });
              }}>
            <img
              src={img.url}
              alt={img.url}
            />
            </div>
          ))}
                
                {this.projectObj.images.map((img, index) => {
                  return (
                    <div
                      className="center-img center-img-thumbnail"
                      key={index}
                      onClick={() => {
                        this.setState({
                          img: {
                            ...this.state.img,
                            index: index
                          }
                        });
                      }}
                    >
                      <img
                        src={require("../../assets/" +
                          this.state.img.folder +
                          "/" +
                          img)}
                        alt={img}
                      />
                    </div>
                  );
                })}
              </div>


          <h1 className="brand">{this.state.product.attributes.brand}</h1>
          <h1 className="model">{this.state.product.name}</h1>


          <p className="price">{this.state.product.price} €</p>
          <p className="quantity">{this.state.product.quantity} units</p>
          <p className="description">{this.state.product.description}</p>
        </React.Fragment>
      );
    }
    return <div className="ProductPage">{product}</div>;
  }
}
