import React, { Component } from "react";
import "./Categorization.css";

export default class Categorization extends Component {
  state = {
    categories: false,
    subcategories: false,
    brands: false,
    weight: false,
    size: false,
    color: false
  }
  render() {
    return (
      <div className="Categorization">
        <table>
          <tr className="hover-row">
            <td>Category</td>
            <td
              className="dropdown-btn-wrapper"
              onClick={() => {
                let originalUsers = this.state.users;
                originalUsers[index].isOpen = !originalUsers[index]
                  .isOpen;
                this.setState({ users: originalUsers });
              }}
            >
              <i className="fas fa-caret-down dropdown-btn" />
            </td>
          </tr>
          {user.isOpen ? (
            <tr>
              <td colSpan="6">
                <div className="more-info">
                  <div>
                    <img
                      src={user.img}
                      alt={user.username}
                      className="profile-img"
                    />
                  </div>
                  <div className="info">
                    <div className="id">
                      <span className="bold">Id:</span> {user.id}
                    </div>
                    <div className="phone">
                      <span className="bold">Phone:</span> {user.phone}
                    </div>
                    <div className="address">
                      <span className="bold">Address:</span>{" "}
                      {user.address}
                    </div>
                  </div>
                </div>
              </td>
            </tr>
          ) : null}
        </table>
      </div>
    );
  }
}
