import React, { Component } from "react";
import "./Categorization.css";
import axios from "axios";
import { getCookie } from "../Util/Util";
import { connect } from "react-redux";
import {
  fetchCatalog,
  createCategory,
  removeCategory,
  createSubcategory,
  removeSubcategory
} from "../../store/actions/indexStore";
import TransitionGroup from "react-transition-group/TransitionGroup";
import CSSTransition from "react-transition-group/CSSTransition";

class Categorization extends Component {
  state = {
    category: "",
    categoryImg: "",
    categoryArr: [],
    selectedCategory: "",
    subcategory: "",
    brand: "",
    weight: "",
    size: "",
    color: "",
    isOpenCategory: false,
    isOpenSubcategory: false,
    isOpenBrand: false,
    isOpenWeight: false,
    isOpenSize: false,
    isOpenColor: false,
    index: 0
  };
  componentDidMount() {
    this.props.onfetchCatalog();
  }
  postSubcategoryHandler() {
    let subcategory = this.state.selectedCategory
      .toLowerCase()
      .split(" ")
      .join("-");
    axios({
      method: "post",
      headers: { "content-type": "application/json" },
      data: JSON.stringify(),
      url: `http://localhost:8086/catalogs/categories/${subcategory}/subcategories?session=${getCookie(
        "Session"
      )}`
    }).then(res => {
      this.setState({ subcategory: "" });
    });
  }
  postBrandHandler() {
    console.log(this.state.brand);
    this.setState({ brand: "" });
  }
  postWeightHandler() {
    console.log(this.state.weight);
    this.setState({ weight: "" });
  }
  postSizeHandler() {
    console.log(this.state.size);
    this.setState({ size: "" });
  }
  postColorHandler() {
    console.log(this.state.color);
    this.setState({ color: "" });
  }
  render() {
    return (
      <div className="Categorization-Height">
        <div className="Categorization">
          <table>
            <tbody>
              <tr
                className="hover-row"
                onClick={() =>
                  this.setState((prevState, props) => {
                    return { isOpenCategory: !prevState.isOpenCategory };
                  })
                }
              >
                <td>Category</td>
                <td className="dropdown-btn-wrapper">
                  <i className="fas fa-caret-down dropdown-btn" />
                </td>
              </tr>
              {this.state.isOpenCategory ? (
                <tr>
                  <td colSpan="2">
                    <div className="more-info">
                      <p className="input-info">Category:</p>
                      <input
                        onChange={e => {
                          this.setState({
                            category: e.target.value
                          });
                        }}
                        value={this.state.category}
                        type="text"
                        placeholder="Category"
                      />
                      <p className="input-info">Category Image:</p>
                      <input
                        onChange={e => {
                          this.setState({
                            categoryImg: e.target.value
                          });
                        }}
                        value={this.state.categoryImg}
                        type="text"
                        placeholder="Category Image"
                      />
                      <div className="buttons">
                        <button
                          className="submit"
                          onClick={() =>
                            this.props.oncreateCategory(
                              this.state.category,
                              this.state.categoryImg
                            )
                          }
                        >
                          Submit
                        </button>
                        <button
                          className="reset"
                          onClick={() =>
                            this.setState({ category: "", categoryImg: "" })
                          }
                        >
                          Reset
                        </button>
                      </div>
                      <TransitionGroup component="ul" className="list-ul">
                        {this.props.catalog.catalog.map(category => (
                          <CSSTransition
                            key={category.id}
                            classNames="fade"
                            timeout={400}
                          >
                            <li className="list-li">
                              {category.name}
                              <i
                                className="fas fa-times btn-icon"
                                onClick={() => {
                                  this.props.onremoveCategory(category.id);
                                }}
                              />
                            </li>
                          </CSSTransition>
                        ))}
                      </TransitionGroup>
                    </div>
                  </td>
                </tr>
              ) : null}
              <tr
                className="hover-row"
                onClick={() =>
                  this.setState((prevState, props) => {
                    return { isOpenSubcategory: !prevState.isOpenSubcategory };
                  })
                }
              >
                <td>Subcategory</td>
                <td className="dropdown-btn-wrapper">
                  <i className="fas fa-caret-down dropdown-btn" />
                </td>
              </tr>
              {this.state.isOpenSubcategory ? (
                <tr>
                  <td colSpan="2">
                    <div className="more-info">
                      <p className="input-info">Category:</p>
                      <select
                      ref={(e) => this.setState({
                        selectedCategory:
                          e.target.options[e.target.selectedIndex].text
                      }) } 
                        onChange={e =>
                          this.setState({
                            selectedCategory:
                              e.target.options[e.target.selectedIndex].text
                          })
                        }
                      >
                        {this.props.catalog.catalog.map(category => (
                          <option key={category.id} value={category.name}>
                            {category.name}
                          </option>
                        ))}
                      </select>
                      <p className="input-info">Subcategory:</p>
                      <input
                        onChange={e => {
                          this.setState({
                            subcategory: e.target.value
                          });
                        }}
                        value={this.state.subcategory}
                        type="text"
                        placeholder="Subcategory"
                      />
                      <div className="buttons">
                        <button
                          className="submit"
                          onClick={() =>
                            this.props.oncreateSubcategory(
                              this.state.selectedCategory,
                              this.state.subcategory
                            )
                          }
                        >
                          Submit
                        </button>
                        <button
                          className="reset"
                          onClick={() => this.setState({ subcategory: "" })}
                        >
                          Reset
                        </button>
                      </div>

                      <ul className="list-ul">
                        {this.props.catalog.catalog.map((category, index) => {
                          if (category.name === this.state.selectedCategory) {
                            return category.subcategories.map(subcategory => (
                              <li
                                className="list-li"
                                key={subcategory.id + "_List"}
                              >
                                {subcategory.name}
                                <i
                                  className="fas fa-times btn-icon"
                                  onClick={() => {
                                    this.props.onremoveSubcategory(
                                      category.id,
                                      subcategory.id
                                    );
                                  }}
                                />
                              </li>
                            ));
                          }
                          if (
                            this.state.selectedCategory === "" &&
                            index === 0
                          ) {
                            return category.subcategories.map(subcategory => (
                              <li className="list-li" key={subcategory.id}>
                                {subcategory.name}
                                <i
                                  className="fas fa-times btn-icon"
                                  onClick={() => {
                                    this.props.onremoveSubcategory(
                                      category.id,
                                      subcategory.id
                                    );
                                  }}
                                />
                              </li>
                            ));
                          }

                          return null;
                        })}
                      </ul>
                    </div>
                  </td>
                </tr>
              ) : null}
              <tr
                className="hover-row"
                onClick={() =>
                  this.setState((prevState, props) => {
                    return { isOpenBrand: !prevState.isOpenBrand };
                  })
                }
              >
                <td>Brand</td>
                <td className="dropdown-btn-wrapper">
                  <i className="fas fa-caret-down dropdown-btn" />
                </td>
              </tr>
              {this.state.isOpenBrand ? (
                <tr>
                  <td colSpan="2">
                    <div className="more-info">
                      <p className="input-info">Brand:</p>
                      <input
                        onChange={e => {
                          this.setState({
                            brand: e.target.value
                          });
                        }}
                        value={this.state.brand}
                        type="text"
                        placeholder="Brand"
                      />
                      <div className="buttons">
                        <button
                          className="submit"
                          onClick={() => this.postBrandHandler()}
                        >
                          Submit
                        </button>
                        <button
                          className="reset"
                          onClick={() => this.setState({ brand: "" })}
                        >
                          Reset
                        </button>
                      </div>
                    </div>
                  </td>
                </tr>
              ) : null}

              <tr
                className="hover-row"
                onClick={() =>
                  this.setState((prevState, props) => {
                    return { isOpenWeight: !prevState.isOpenWeight };
                  })
                }
              >
                <td>Weight</td>
                <td className="dropdown-btn-wrapper">
                  <i className="fas fa-caret-down dropdown-btn" />
                </td>
              </tr>
              {this.state.isOpenWeight ? (
                <tr>
                  <td colSpan="2">
                    <div className="more-info">
                      <p className="input-info">Weight:</p>
                      <input
                        onChange={e => {
                          this.setState({
                            weight: e.target.value
                          });
                        }}
                        value={this.state.weight}
                        type="text"
                        placeholder="Weight"
                      />
                      <div className="buttons">
                        <button
                          className="submit"
                          onClick={() => this.postWeightHandler()}
                        >
                          Submit
                        </button>
                        <button
                          className="reset"
                          onClick={() => this.setState({ weight: "" })}
                        >
                          Reset
                        </button>
                      </div>
                    </div>
                  </td>
                </tr>
              ) : null}
              <tr
                className="hover-row"
                onClick={() =>
                  this.setState((prevState, props) => {
                    return { isOpenSize: !prevState.isOpenSize };
                  })
                }
              >
                <td>Size</td>
                <td className="dropdown-btn-wrapper">
                  <i className="fas fa-caret-down dropdown-btn" />
                </td>
              </tr>
              {this.state.isOpenSize ? (
                <tr>
                  <td colSpan="2">
                    <div className="more-info">
                      <p className="input-info">Size:</p>
                      <input
                        onChange={e => {
                          this.setState({
                            size: e.target.value
                          });
                        }}
                        value={this.state.size}
                        type="text"
                        placeholder="Size"
                      />
                      <div className="buttons">
                        <button
                          className="submit"
                          onClick={() => this.postSizeHandler()}
                        >
                          Submit
                        </button>
                        <button
                          className="reset"
                          onClick={() => this.setState({ size: "" })}
                        >
                          Reset
                        </button>
                      </div>
                    </div>
                  </td>
                </tr>
              ) : null}
              <tr
                className="hover-row"
                onClick={() =>
                  this.setState((prevState, props) => {
                    return { isOpenColor: !prevState.isOpenColor };
                  })
                }
              >
                <td>Color</td>
                <td className="dropdown-btn-wrapper">
                  <i className="fas fa-caret-down dropdown-btn" />
                </td>
              </tr>
              {this.state.isOpenColor ? (
                <tr>
                  <td colSpan="2">
                    <div className="more-info">
                      <p className="input-info">Color:</p>
                      <input
                        onChange={e => {
                          this.setState({
                            color: e.target.value
                          });
                        }}
                        value={this.state.color}
                        type="text"
                        placeholder="Color"
                      />
                      <div className="buttons">
                        <button
                          className="submit"
                          onClick={() => this.postColorHandler()}
                        >
                          Submit
                        </button>
                        <button
                          className="reset"
                          onClick={() => this.setState({ color: "" })}
                        >
                          Reset
                        </button>
                      </div>
                    </div>
                  </td>
                </tr>
              ) : null}
            </tbody>
          </table>
        </div>
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    catalog: state.catalog
  };
};

const mapDispatchToProps = dispatch => {
  return {
    onfetchCatalog: () => dispatch(fetchCatalog()),
    oncreateCategory: (name, imageUrl) =>
      dispatch(createCategory(name, imageUrl)),
    onremoveCategory: categoryId => dispatch(removeCategory(categoryId)),
    oncreateSubcategory: (subcategoryId, subcategoryName) =>
      dispatch(createSubcategory(subcategoryId, subcategoryName)),
    onremoveSubcategory: (categoryId, subcategoryId) =>
      dispatch(removeSubcategory(categoryId, subcategoryId))
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Categorization);
