import React from "react";
import "./ProductInfo.css";

export default function ProductInfo(props) {
  return (
    <div className="ProductInfo">
      <header>
        <h1 className="brand">{props.product.attributes.brand}</h1>
        <h1 className="model">{props.product.name}</h1>
      </header>
      <section>
        <div
          style={{ backgroundColor: props.product.attributes.color }}
          className="color"
        />
        <p className="price">{props.product.price} â‚¬</p>
        <p className="quantity">{props.product.quantity} units</p>
        <p className="description">{props.product.description}</p>
      </section>
    </div>
  );
}
