import React, { Component } from "react";
import "./Entry.css";
import Login from "./Login/Login";
import Register from "./Register/Register";
import CSSTransition from "react-transition-group/CSSTransition";

export default class Entry extends Component {
  state = {
    showRegister: false
  };
  toggleRegisterHandler = () => {
    this.setState((prevState, props) => {
      return { showRegister: !prevState.showRegister };
    });
  };
  render() {
    const animationTiming = {
      enter: 400,
      exit: 1000
    };
    return (
      <div className="Entry">
        {this.state.showRegister ? (
          <CSSTransition
            in={true}
            timeout={animationTiming}
            mountOnEnter
            unmountOnExit
            classNames={{
              enter: "order-message-enter",
              enterActive: "order-message-open",
              exit: "order-message-exit",
              exitActive: "order-message-closed"
            }}
          >
            {state => (
              <Register
                toggleRegister={() => this.toggleRegisterHandler()}
                {...this.props}
              />
            )}
          </CSSTransition>
        ) : (
          <Login
            toggleRegister={() => this.toggleRegisterHandler()}
            {...this.props}
          />
        )}
      </div>
    );
  }
}
