import React, { Component } from "react";
import "./Users.css";
import axios from "axios";
import { getCookie } from "../Util/Util";

export default class Users extends Component {
  state = {
    users: []
  };
  componentDidMount() {
    let url = `http://localhost:8087/secure/users?access_token=${getCookie(
      "Session"
    )}`;
    axios.get(url).then(res => {
      let users = [];
      let array = res.data;
      for (let i = 0; i < array.length; i++) {
        users.push({ ...array[i], isOpen: false });
      }
      this.setState({
        users: users
      });
    });
  }
  render() {
    return (
      <div className="Users">
        {this.state.users.length === 0 ? null : (
          <table>
            <thead>
              <tr>
                <th>Username</th>
                <th>Name</th>
                <th>Email</th>
                <th>Role</th>
                <th>Enable</th>
              </tr>
            </thead>
            <tbody>
              {this.state.users.map((user, index) => (
                <React.Fragment key={user.id}>
                  <tr className="hover-row">
                    <td>{user.username}</td>
                    <td>
                      {user.name} {user.surname}
                    </td>
                    <td>{user.email}</td>
                    <td>
                      {user.roles[0].name === "ROLE_USER" ? (
                        <p>User</p>
                      ) : user.roles[0].name === "ROLE_ADMIN" ? (
                        <p>Admin</p>
                      ) : null}
                    </td>
                    <td>
                      {user.enabled ? (
                        <div className="green" />
                      ) : (
                        <div className="red" />
                      )}
                    </td>
                    <td
                      className="dropdown-btn-wrapper"
                      onClick={() => {
                        let originalUsers = this.state.users;
                        originalUsers[index].isOpen = !originalUsers[index]
                          .isOpen;
                        this.setState({ users: originalUsers });
                      }}
                    >
                      <i className="fas fa-caret-down dropdown-btn" />
                    </td>
                  </tr>
                  {user.isOpen ? (
                    <tr>
                      <td colSpan="6">
                        <div className="more-info">
                          <div>
                            <img
                              src={user.img}
                              alt={user.username}
                              className="profile-img"
                            />
                          </div>
                          <div className="info">
                            <div className="id">
                              <span className="bold">Id:</span> {user.id}
                            </div>
                            <div className="phone">
                              <span className="bold">Phone:</span> {user.phone}
                            </div>
                            <div className="address">
                              <span className="bold">Address:</span>{" "}
                              {user.address}
                            </div>
                          </div>
                        </div>
                      </td>
                    </tr>
                  ) : null}
                </React.Fragment>
              ))}
            </tbody>
          </table>
        )}
      </div>
    );
  }
}
