import React, { Component } from "react";
import "./Login.css";
import axios from "axios";

export default class Login extends Component {
  state = {
    user: {
      username: "",
      password: ""
    }
  };

  loginHandler() {
    console.log(this.state.user);

    const options = {
      method: "POST",
      url: `http://localhost:8087/oauth/token?grant_type=password&username=${
        this.state.user.username
      }&password=${this.state.user.password}`,
      auth: {
        username: "ecommerce-micros",
        password: "ecommerce"
      },
      data: {
        scope: "public"
      }
    };
    axios(options)
      .then(res => {
        res.setStatus(200);
        //console.log(res);
      })
      .catch(error => {
        //console.log(error);
      });
  }

  render() {
    return (
      <div className="Login">
        <h1>Login</h1>
        <div className="wrapper-login-fields">
          <input
            className="username"
            type="text"
            placeholder="Username"
            onChange={e => {
              this.setState({
                user: { ...this.state.user, username: e.target.value }
              });
            }}
          />
          <input
            className="password"
            type="password"
            placeholder="Password"
            onChange={e => {
              this.setState({
                user: { ...this.state.user, password: e.target.value }
              });
            }}
          />
          <button onClick={() => this.loginHandler()}>Login</button>
          <p>
            Not registered?{" "}
            <span
              onClick={() => {
                this.props.toggleRegister();
              }}
            >
              Create an account
            </span>
          </p>
        </div>
      </div>
    );
  }
}
