import React, { Component } from "react";
import "./ProductInfo.css";
import AddedCartMessage from "../AddedCartMessage/AddedCartMessage";

export default class ProductInfo extends Component {
  state = {
    isOpenCartMessage: false,
    quantity: 0
  };
  addLocalstorage() {
    let products = localStorage.getItem("product");
    let arrProducts = [];
    let quantity = localStorage.getItem("quantity");
    let arrQuantity = [];
    if (products !== null && products !== "") {
      arrProducts = products.split(",");
      arrQuantity = quantity.split(",");
      if (arrProducts.indexOf(this.props.product.id) === -1) {
        arrProducts.push(this.props.product.id);
        arrQuantity.push(1);
      } else {
        const indexOfProduct = arrProducts.indexOf(this.props.product.id);
        let prevQuantity = parseInt(arrQuantity[indexOfProduct], 10) + 1;
        arrQuantity[indexOfProduct] = prevQuantity;
      }
    } else {
      arrProducts = [this.props.product.id];
      arrQuantity = [1];
    }
    localStorage.setItem("product", arrProducts);
    localStorage.setItem("quantity", arrQuantity);
  }
  closeCartMessage() {
    this.setState({ isOpenCartMessage: false });
  }
  FirstLetterToUppercase() {
    let size = this.props.product.attributes.size;
    return size[0].toUpperCase() + size.slice(1, size.length);
  }
  render() {
    return (
      <React.Fragment>
        <div className="ProductInfo">
          <header>
            <h1 className="brand">{this.props.product.attributes.brand}</h1>
            <h1 className="model">{this.props.product.name}</h1>
          </header>
          <section>
            <div className="color">
              <h3>Color</h3>
              <div
                style={{ backgroundColor: this.props.product.attributes.color }}
              />
            </div>
            <div className="size">
              <h3>Size</h3>
              <h4 className="size-text">{this.FirstLetterToUppercase()}</h4>
            </div>
            <div className="quantity">
              <h3>Quantity</h3>
              <h4>{this.props.product.quantity} units</h4>
            </div>
            <div className="price">
              <h3>Price</h3>
              <h4>{this.props.product.price} €</h4>
            </div>
            <div className="description">
              <h3>Description</h3>
              <i
                className="fas fa-plus btn-plus"
                id="btn-plus"
                onClick={() => {
                  if (
                    document.getElementById("description").style.display ===
                    "block"
                  ) {
                    document.getElementById("btn-plus").style.transform =
                      "rotate(90deg)";
                    document.getElementById("description").style.display =
                      "none";
                  } else {
                    document.getElementById("btn-plus").style.transform =
                      "rotate(45deg)";
                    document.getElementById("description").style.display =
                      "block";
                  }
                }}
              />
              <p id="description">{this.props.product.description}</p>
            </div>
            <button className="btn-list">Add to list</button>
            <button
              className="btn-basket"
              onClick={() => {
                this.setState({ isOpenCartMessage: true });
                this.addLocalstorage();
              }}
            >
              Add to basket
            </button>
          </section>
        </div>
        {this.state.isOpenCartMessage ? (
          <AddedCartMessage
            closeCartMessage={() => this.closeCartMessage()}
            brand={this.props.product.attributes.brand}
            name={this.props.product.name}
            image={this.props.product.thumbnail}
          />
        ) : null}
      </React.Fragment>
    );
  }
}
