import React, { Component } from "react";
import "./Carousel.css";

export default class Carousel extends Component {
  state = {
    slideIndex: 1
  };
  componentDidMount() {
    this.createInterval();
  }
  createInterval() {
    this.interval = setInterval(
      () =>
        this.setState({ slideIndex: this.showSlides(this.state.slideIndex) }),
      4000
    );
  }
  componentWillUnmount() {
    clearInterval(this.interval);
  }
  changeImage() {}
  showSlides(slideIndex) {
    const slides = document.getElementsByClassName("mySlides");
    const dots = document.getElementsByClassName("dot");
    for (let i = 0; i < slides.length; i++) {
      slides[i].style.display = "none";
    }
    slideIndex++;
    if (slideIndex > slides.length) {
      slideIndex = 1;
    }
    for (let i = 0; i < dots.length; i++) {
      dots[i].className = dots[i].className.replace(" active", "");
    }
    slides[slideIndex - 1].style.display = "block";
    dots[slideIndex - 1].className += " active";
    return slideIndex;
  }
  render() {
    return (
      <div className="Carousel">
        <div className="slideshow-container">
          <div className="mySlides fade" style={{ display: "block" }}>
            <img
              src="https://static.photocdn.pt/images/articles/2017_1/iStock-545347988.jpg"
              alt="carousel"
            />
          </div>
          <div className="mySlides fade">
            <img
              src="https://fd204d43461da5218393-0b3ca8ff9ad90f3780bc876f4d2d02ae.ssl.cf1.rackcdn.com/uploads/2018/07/AV_Landscape-Hero-Contour-2993-1276x800.jpg"
              alt="carousel"
            />
          </div>
          <div className="mySlides fade">
            <img
              src="https://cdn.mos.cms.futurecdn.net/FUE7XiFApEqWZQ85wYcAfM.jpg"
              alt="carousel"
            />
          </div>
        </div>
        <div style={{ textAlign: "center" }}>
          <span
            className="dot active"
            onClick={() => {
              clearInterval(this.interval);
              this.showSlides(0);
              this.createInterval();
            }}
          />
          <span
            className="dot"
            onClick={() => {
              clearInterval(this.interval);
              this.showSlides(1);
              this.createInterval();
            }}
          />
          <span
            className="dot"
            onClick={() => {
              clearInterval(this.interval);
              this.showSlides(2);
              this.createInterval();
            }}
          />
        </div>
      </div>
    );
  }
}
