import * as actionTypes from "./actionTypes";

export const addProduct = product => {
  return {
    type: actionTypes.ADD_PRODUCT,
    product: product
  };
};

export const removeProduct = productId => {
  return {
    type: actionTypes.REMOVE_PRODUCT,
    productId: productId
  };
};
