import React, { Component } from "react";
import "./Login.css";
import axios from "axios";
import { connect } from "react-redux";
import { fetchAuthenticatedUserLogin } from "../../../store/actions/indexStore";

class Login extends Component {
  state = {
    error: "",
    flash: true
  };

  render() {
    return (
      <div className="Login">
        <h1>Login</h1>
        <div className="wrapper-login-fields">
          <input
            className="username"
            type="text"
            placeholder="Username"
            onChange={e => {
              this.setState({
                user: { ...this.state.user, username: e.target.value }
              });
            }}
          />
          <input
            className="password"
            type="password"
            placeholder="Password"
            onChange={e => {
              this.setState({
                user: { ...this.state.user, password: e.target.value }
              });
            }}
          />
          <p
            style={{
              visibility:
                this.state.token === "" && this.state.error !== ""
                  ? "visible"
                  : "hidden"
            }}
            onAnimationEnd={() => this.setState({ flash: false })}
            className={this.state.flash ? "warning flash" : "warning"}
          >
            {this.state.error}
          </p>
          <button
            onClick={() => {
              //this.loginHandler();
              this.props.onfetchAuthenticatedUserLogin(
                this.state.user.username,
                this.state.user.password
              );
              this.setState({ flash: true });
            }}
          >
            Login
          </button>
          <p className="register">
            Not registered?{" "}
            <span
              onClick={() => {
                this.props.toggleRegister();
              }}
            >
              Create an account
            </span>
          </p>
        </div>
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    user: state.user
  };
};

const mapDispatchToProps = dispatch => {
  return {
    onfetchAuthenticatedUserLogin: (username, password) =>
      dispatch(fetchAuthenticatedUserLogin(username, password))
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Login);
