import React, { Component } from "react";
import "./Home.css";
import Carousel from "../UI/Carousel/Carousel";
import { connect } from "react-redux";
import { Link } from "react-router-dom";

class Home extends Component {
  render() {
    return (
      <div className="Home">
        <div className="home">
          <div className="home-wrapper">
            <div className="greeting">
              {this.props.user.authenticatedUser.name === undefined
                ? null
                : `Hello, ${this.props.user.authenticatedUser.name} ${
                    this.props.user.authenticatedUser.surname
                  }`}
            </div>
            <div className="home-links">
              <Link to="/5ceabe7f-a61f-4cd7-bacc-e1ed837811be/c93b538a-fa4d-4b6e-889b-c54df57e2129">Headphones</Link>
              <Link to="/7c2be2c8-8560-4406-a093-e9dbb10aa396/251c2e9d-08d1-48aa-9267-85aeb3f88519">Android Smartphones</Link>
              <Link to="/orders">Cart</Link>
            </div>
          </div>
        </div>
        <Carousel />
      </div>
    );
  }
}
const mapStateToProps = state => {
  return {
    user: state.user
  };
};

export default connect(mapStateToProps)(Home);
