import React, { Component } from "react";
import "./Orders.css";
import axios from "axios";

export default class Orders extends Component {
  state = {
    products: [],
    quantityLs: []
  }
  componentDidMount() {
    if (localStorage.getItem("product") !== null) {
      let products = localStorage.getItem("product").split(",");
      axios({
        method: "post",
        headers: { "content-type": "application/json" },
        data: products,
        url: "http://localhost:8086/products/arr"
      })
        .then(res => {
          let quantity = localStorage.getItem("quantity").split(",");
          this.setState({ products: res.data, quantity: quantity });
        });
    }
  }
  removeFromCartLs(productId) {
    console.log();
    for(let p = 0; i < this.state.products.length; p++) {
      if (this.state.products[i].id === productId) {
        products.splice(index, 1);
      }
    }
    let products = localStorage.getItem("product").split(",");
    let quantity = localStorage.getItem("quantity").split(",");
    const index = products.indexOf(productId);
    products.splice(index, 1);
    quantity.splice(index, 1);
    localStorage.setItem("product", products);
    localStorage.setItem("quantity", quantity);
    this.setState({
      products: products,
      quantity: quantity
    })
  }
  render() {
    return <div className="Orders">
      <div className="cart-products">
        <div className="cart-products-wrapper">
          <table>
            <thead>
              <tr>
                <th>Item</th>
                <th>Description</th>
                <th>Price</th>
                <th>Quantity</th>
                <th>Subtotal</th>
              </tr>
            </thead>
            <tbody>
              {this.state.products.length !== 0 ? this.state.products.map((product, index) => <tr key={product.id}>
                <td>
                  <div className="frame-img">
                    <img src={product.thumbnail} alt={product.id}></img>
                  </div>
                </td>
                <td><div>
                  <div className="product-brand">{product.attributes.brand}</div>
                  <div className="product-name">{product.name}</div>
                </div></td>
                <td>{"€" + product.price}</td>
                <td>
                  <input type="number" name="quantity" min="0" value={this.state.quantity[index]} max={product.quantity} onChange={(e) => {
                    if (e.target.value > product.quantity) {
                      e.target.value = product.quantity
                    }
                    let quantity = this.state.quantity;
                    quantity[index] = e.target.value;
                    this.setState({ quantity: quantity });
                    localStorage.setItem("quantity", quantity);

                          if (e.target.value < 1) {
                            this.removeFromCartLs(product.id);
                          }
                  }}></input>
                </td>
                <td>{"€" + (Math.floor(product.price * product.quantity * 100) / 100)}</td>
              </tr>) : <tr><td colSpan="5" className="no-products">Your cart is empty!</td></tr>}
            </tbody>
          </table>
        </div>
      </div>
      <div className="price">
        <div className="price-wrapper">
          <div className="total-price">
            <p>Total to pay</p>
            <p className="price-number">€500.00</p>
          </div>
          <button className="checkout-btn">Proceed to checkout</button>
        </div>
      </div>
    </div>;
  }
}
