import React, { Component } from "react";
import "./Categorization.css";

export default class Categorization extends Component {
  state = {
    categories: "",
    subcategories: "",
    brands: "",
    weight: "",
    size: "",
    color: "",
    isOpenCategories: false,
    isOpenSubcategories: false,
    isOpenBrands: false,
    isOpenWeight: false,
    isOpenSize: false,
    isOpenColor: false
  }

  render() {
    return (
      <div className="Categorization">
        <table>
          <tbody>
            <tr className="hover-row"
              onClick={() => this.setState((prevState, props) => {
                return { categories: !prevState.categories };
              })}>
              <td>Category</td>
              <td
                className="dropdown-btn-wrapper"
              >
                <i className="fas fa-caret-down dropdown-btn" />
              </td>
            </tr>
            {this.state.isOpenCategories ? (
              <tr>
                <td colSpan="2">
                  <div className="more-info">
                    <input
                      onChange={e => {
                        this.setState({
                          categories: e.target.value
                        }
                        );
                      }}
                      value={this.state.categories}
                      type="text"
                      placeholder="Category"
                    />
                  </div>
                  <button onClick={() => console.log(this.state.categories)}>Submit</button>
                  <button onClick={() => console.log("reset")
                  }>Reset</button>
                </td>
              </tr>
            ) : null}
          </tbody>
        </table>
      </div>
    );
  }
}
