import React, { Component } from "react";
import "./DropdownMenu.css";
import { Link } from "react-router-dom";

export default class DropdownMenu extends Component {
  state = {
    categories: []
  };
  async componentDidMount() {
    const response = await fetch(`http://localhost:8086/catalogs/categories`);
    const json = await response.json();
    this.setState({ categories: json });
  }
  render() {
    return (
      <div className="DropdownMenu">
        <div className="category">
          {this.state.categories.map(category => (
            <div key={category.id} className="dropdown" onClick={() => document.getElementsByClassName("dropdown-content").style.display = "none"}>
              <div className="dropbtn">{category.name}</div>
              <div className="dropdown-content">
                <div className="dropdown-layout">
                  <div className="menu">
                    {category.subcategories.map(subcategory => (
                      <Link
                        key={subcategory.id}
                        to={"/" + category.id + "/" + subcategory.id}
                      >
                        {subcategory.name}
                      </Link>
                    ))}
                  </div>
                  <div className="image">
                    <img src={category.img} alt={category.id} />
                  </div>
                </div>
                <div />
              </div>
            </div>
          ))}
        </div>
      </div>
    );
  }
}
