import React from "react";
import "./ProductInfo.css";
import { withRouter } from 'react-router-dom';

const ProductInfo = (props) => {
  const addLocalstorage = () => {
    let products = localStorage.getItem("product");
    let arrProducts = [];
    if (products !== null) {
      arrProducts = products.split(",");
      if (arrProducts.indexOf(props.product.id) === -1) {
        arrProducts.push(props.product.id)
      }
    } else {
      arrProducts = [props.product.id];
    }
    localStorage.setItem("product", arrProducts)
  };

  return (
    <div className="ProductInfo">
      <header>
        <h1 className="brand">{props.product.attributes.brand}</h1>
        <h1 className="model">{props.product.name}</h1>
      </header>
      <section>
        <div className="color">
          <h3>Color</h3>
          <div style={{ backgroundColor: props.product.attributes.color }} />
        </div>
        <div className="size">
          <h3>Size</h3>
          <select>
            <option value="medium">Medium</option>
            <option value="small">Small</option>
            <option value="big">Big</option>
          </select>
        </div>
        <div className="quantity">
          <h3>Quantity</h3>
          <h4>{props.product.quantity} units</h4>
        </div>
        <div className="price">
          <h3>Price</h3>
          <h4>{props.product.price} â‚¬</h4>
        </div>
        <div className="description">
          <h3>Description</h3>
          <i
            className="fas fa-plus btn-plus"
            id="btn-plus"
            onClick={() => {
              if (
                document.getElementById("description").style.display === "block"
              ) {
                document.getElementById("btn-plus").style.transform =
                  "rotate(90deg)";
                document.getElementById("description").style.display = "none";
              } else {
                document.getElementById("btn-plus").style.transform =
                  "rotate(45deg)";
                document.getElementById("description").style.display = "block";
              }
            }}
          />
          <p id="description">{props.product.description}</p>
        </div>
        <div className="btn-list">Add to list</div>
        <div className="btn-basket" onClick={() => {
          addLocalstorage();
          props.history.push({ pathname: "/orders" });
        }}>Add to basket</div>
      </section>
    </div>
  );
}

export default withRouter(ProductInfo);
