import React, { Component } from "react";
import "./Register.css";

export default class Register extends Component {
  state = {
    user: {
      name: "",
      surname: "",
      email: "",
      phone: "",
      address: "",
      username: "",
      password: ""
    }
  };

  render() {
    return (
      <div className="Register">
        <header>
          <i
            className="fas fa-arrow-left"
            onClick={() => {
              this.props.toggleRegister();
            }}
          />
          <h1>Register</h1>
        </header>
        <div className="wrapper-register-fields">
          <input
            className="name"
            type="text"
            placeholder="Name"
            value={this.state.user.name}
            onChange={e => {
              this.setState({
                user: { name: e.target.value, ...this.state.user }
              });
            }}
          />
          <input
            className="surname"
            type="text"
            placeholder="Surname"
            onChange={e => {
              this.setState({
                user: { surname: e.target.value, ...this.state.user }
              });
            }}
          />
          <input
            className="email"
            type="email"
            placeholder="Email"
            onChange={e => {
              this.setState({
                user: { email: e.target.value, ...this.state.user }
              });
            }}
          />
          <input
            className="phone"
            type="text"
            placeholder="Phone"
            onChange={e => {
              this.setState({
                user: { phone: e.target.value, ...this.state.user }
              });
            }}
          />
          <input
            className="address"
            type="text"
            placeholder="Address"
            onChange={e => {
              this.setState({
                user: { address: e.target.value, ...this.state.user }
              });
            }}
          />
          <input
            className="username"
            type="text"
            placeholder="Username"
            onChange={e => {
              this.setState({
                user: { username: e.target.value, ...this.state.user }
              });
            }}
          />
          <input
            className="password"
            type="password"
            placeholder="Password"
            onChange={e => {
              this.setState({
                user: { password: e.target.value, ...this.state.user }
              });
            }}
          />
          <button onClick={() => console.log(this.state.user)
          }>Register</button>
        </div>
      </div>
    );
  }
}
