import React, { Component } from "react";
import "./Products.css";

export default class Products extends Component {
  state = {
    products: []
  };
  async componentDidMount() {
    try {
      fetch(`http://localhost:8086/catalogs/categories/audio/subcategories/headphones`)
      .then(res => res.json())
      .then(json => this.setState({ products: json }));
    } catch(error) {
      console.log(error);
    }
  }

  render() {
    return (
      <div>
        <h2>Products</h2>
        <ul>
            {this.state.products.map(el => <li>
              {el.name}
            </li>)}
        </ul>
      </div>
    );
  }
}
