import React, { Component } from "react";
import "./Login.css";
import axios from "axios";

export default class Login extends Component {
  state = {
    user: {
      username: "",
      password: ""
    },
    token: "",
    error: false,
    flash: true
  };

  loginHandler() {
    axios
      .request({
        url: `/oauth/token?grant_type=password&username=${
          this.state.user.username
        }&password=${this.state.user.password}`,
        method: "post",
        baseURL: "http://localhost:8087/",
        auth: {
          username: "ecommerce-micros",
          password: "ecommerce"
        }
      })
      .then(res => {
        // if 400 password or user incorrect
        if (res.status === 200) {
          this.setState({ token: res.data.access_token, error: false });
          console.log(this.state.token);
        }
        console.log(res);
      })
      .catch(error => {
        this.setState({ token: "", error: true });
        console.log(error);
      });
  }

  render() {
    return (
      <div className="Login">
        <h1>Login</h1>
        <div className="wrapper-login-fields">
          <input
            className="username"
            type="text"
            placeholder="Username"
            onChange={e => {
              this.setState({
                user: { ...this.state.user, username: e.target.value }
              });
            }}
          />
          <input
            className="password"
            type="password"
            placeholder="Password"
            onChange={e => {
              this.setState({
                user: { ...this.state.user, password: e.target.value }
              });
            }}
          />
          {this.state.token === "" && this.state.error ? (
            <p
              onAnimationEnd={() => this.setState({ flash: false })}
              className={this.state.flash ? "warning flash" : "warning"}
            >
              The username or password you entered isn`t correct. Try entering
              it again.
            </p>
          ) : null}
          <button onClick={() => this.loginHandler()}>Login</button>
          <p className="register">
            Not registered?{" "}
            <span
              onClick={() => {
                this.props.toggleRegister();
               
                  this.setState({ flash: true });
                
              }}
            >
              Create an account
            </span>
          </p>
        </div>
      </div>
    );
  }
}
