import React from "react";
import "./UserMenu.css";
import { Link } from "react-router-dom";
import { withRouter } from "react-router-dom";
import { connect } from "react-redux";
import { resetAuthenticatedUser } from "../../store/actions/indexStore";

const UserMenu = props => {
  return (
    <div className="UserMenu" onClick={() => props.closeFullscreen()}>
      <p className="greeting">Hello, {props.name}.</p>
      {props.role === "ROLE_ADMIN" ? (
        <React.Fragment>
          <Link to={"/secure/users"}>Manage Users</Link>
          <Link to={"/secure/categorization"}>Manage Categorization</Link>
          <Link to={"/secure/products"}>Manage Products</Link>
          <Link to={"/secure/orders"}>Manage Orders</Link>
        </React.Fragment>
      ) : null}
      <Link to={"/user"}>Settings</Link>
      <div
        className="logout"
        onClick={() => {
          document.cookie =
            "Session=; expires= Thu, 21 Aug 2014 20:00:00 UTC; path=/ ";
          props.history.push({ pathname: "/" });
          this.props.onresetAuthenticatedUser();
        }}
      >
        Log Out
      </div>
    </div>
  );
};

const mapDispatchToProps = dispatch => {
  return {
    onresetAuthenticatedUser: () => dispatch(resetAuthenticatedUser())
  };
};

export default connect(
  null,
  mapDispatchToProps
)(withRouter(UserMenu));
