import React, { Component } from "react";
import "./CreateProduct.css";
import axios from "axios";

export default class CreateProduct extends Component {
  state = {
    categories: {},
    selectedCategorie: "",
    imageUrl: "",
    imageArr: []
  };
  componentDidMount() {
    let url = `http://localhost:8086/catalogs/categories`;
    axios.get(url).then(res => {
      let categories = {};
      for (let i = 0; i < res.data.length; i++) {
        let categorie = res.data[i].name;
        categories[categorie] = [];
        for (let r = 0; r < res.data[i].subcategories.length; r++) {
          categories[categorie].push(res.data[i].subcategories[r].name);
        }
      }
      this.setState({
        categories: categories,
        selectedCategorie: res.data[0].name
      });
    });
  }

  render() {
    return (
      <div className="CreateProduct">
        <div className="product-wrapper">
          <select
            className="categories"
            onChange={e =>
              this.setState({
                selectedCategorie: e.target.options[e.target.selectedIndex].text
              })
            }
          >
            {Object.keys(this.state.categories).map(categorie => (
              <option key={categorie} value={categorie}>
                {categorie}
              </option>
            ))}
          </select>

          <select className="subcategories">
            {this.state.categories[this.state.selectedCategorie] !== undefined
              ? this.state.categories[this.state.selectedCategorie].map(
                  subcategorie => (
                    <option key={subcategorie} value={subcategorie}>
                      {subcategorie}
                    </option>
                  )
                )
              : null}
          </select>
          <textarea
            className="description"
            type="text"
            placeholder="Description"
          />
          <div className="thumbnail">
            <p className="label">Thumbnail URL:</p>
            <input type="text" placeholder="Thumbnail" />
          </div>
          <div className="name">
            <p className="label">Name:</p>
            <input type="text" placeholder="Name" />
          </div>
          <div className="price">
            <p className="label">Price:</p>
            <input type="text" placeholder="Price" />
          </div>
          <div className="attributes">
            <select>
              <option value="black">Black</option>
              <option value="gray">Gray</option>
              <option value="blue">Blue</option>
              <option value="green">Green</option>
              <option value="purple">Purple</option>
              <option value="pink">Pink</option>
              <option value="yellow">Yellow</option>
              <option value="orange">Orange</option>
              <option value="red">Red</option>
              <option value="brown">Brown</option>
              <option value="white">White</option>
              <option value="others">Others</option>
            </select>
            <div className="brand">
              <select>
                <option value="black">Black</option>
                <option value="gray">Gray</option>
              </select>
            </div>
            <div className="weight">
              <select>
                <option value="black">Black</option>
                <option value="gray">Gray</option>
              </select>
            </div>
            <div className="size">
              <select>
                <option value="black">Black</option>
                <option value="gray">Gray</option>
              </select>
            </div>
          </div>
          <div className="images">
            <p className="label">Image Url:</p>
            <div className="image-wrapper">
              <input
                onChange={e => this.setState({ imageUrl: e.target.value })}
                value={this.state.imageUrl}
                type="text"
                placeholder="Image URL"
              />
              <i
                onClick={() => {
                  if (this.state.imageArr.indexOf(this.state.imageUrl) === -1) {
                  console.log("err");
                  
                  }
                  this.state.imageArr.push(this.state.imageUrl);
                  this.setState({ imageUrl: "" });
                }}
                className="fas fa-plus"
              />
            </div>
          </div>
          <div className="image-list">
            {this.state.imageArr.map(url => (
              <p className="image" key={url}>{url}</p>
            ))}
          </div>
        </div>
      </div>
    );
  }
}
