import React, { Component } from "react";
import "./Search.css";
import Icons from "../../components/UI/Icons/Icons";
import axios from "axios";

export default class extends Component {
  state = {
    products: []
  };
  fetchData(value = "") {
    axios.get(`http://localhost:8086/products`).then(res => {
      let productsSearch = [];
      if (value !== "") {
        for (let i = 0; i < res.data.length; i++) {
          if (
            res.data[i].name.slice(0, value.length).toLowerCase() ===
            value.toLowerCase()
          ) {
            productsSearch.push(res.data[i]);

          }
        }
      }
      this.setState({ products: productsSearch });
    });
  }

  render() {
    return (
      <div className="Search">
        <div className="search-icon">
          <Icons type="search" />
        </div>
        <input
          placeholder="Search for anything"
          onChange={e => this.fetchData(e.target.value)}
        />
        {console.log(this.state.products)}
      </div>
    );
  }
}
