    user.account_non_locked = enable;
    user.credentialsNonExpired = enable;
    axios
      .put(
        `http://localhost:8087/secure/users/${user.id}?access_token=${getCookie(
          "Session"
        )}`,
        user
      )
      .then(res => {
        if (res.status === 200) {
          let userArr = this.state.users;
          userArr[index] = user;
          this.setState({ users: userArr });
        }
      });
  }*/
  render() {
    return (
      <div className="Users">

      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    users: state.users
  };
};

const mapDispatchToProps = dispatch => {
  return {
    onfetchUsers: () => dispatch(fetchUsers()),
    onUserAdded: user => dispatch(addUser(user)),
    onUserRemoved: username => dispatch(removeUser(username))
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Users);
