import React, { Component } from "react";
import "./Login.css";
import axios from "axios";

export default class Login extends Component {
  state = {
    user: {
      username: "",
      password: ""
    },
    token: "",
    error: "",
    flash: true
  };

  loginHandler() {
    axios
      .request({
        url: `/oauth/token?grant_type=password&username=${
          this.state.user.username
        }&password=${this.state.user.password}`,
        method: "post",
        baseURL: "http://localhost:8087/",
        auth: {
          username: "ecommerce-micros",
          password: "ecommerce"
        }
      })
      .then(res => {
        if (res.status === 200) {
          this.setState({ token: res.data.access_token, error: "" });
          this.props.closeEntry();
        }
      })
      .catch(error => {
        let message = "";
        if (error.response.data.error_description === "Bad credentials") {
          message =
            "The username or password you entered isn`t correct. Try entering it again.";
        } else {
          message = error.response.data.error_description;
        }
        this.setState({ token: "", error: message });
      });
  }

  render() {
    return (
      <div className="Login">
        <h1>Login</h1>
        <div className="wrapper-login-fields">
          <input
            className="username"
            type="text"
            placeholder="Username"
            onChange={e => {
              this.setState({
                user: { ...this.state.user, username: e.target.value }
              });
            }}
          />
          <input
            className="password"
            type="password"
            placeholder="Password"
            onChange={e => {
              this.setState({
                user: { ...this.state.user, password: e.target.value }
              });
            }}
          />
          <p
            style={{
              visibility:
                this.state.token === "" && this.state.error !== ""
                  ? "visible"
                  : "hidden"
            }}
            onAnimationEnd={() => this.setState({ flash: false })}
            className={this.state.flash ? "warning flash" : "warning"}
          >
            {this.state.error}
          </p>
          <button
            onClick={() => {
              this.loginHandler();

              this.setState({ flash: true });
            }}
          >
            Login
          </button>
          <p className="register">
            Not registered?{" "}
            <span
              onClick={() => {
                this.props.toggleRegister();
              }}
            >
              Create an account
            </span>
          </p>
        </div>
      </div>
    );
  }
}
