import React, { Component } from "react";
import "./EditUser.css";
import { getCookie } from "../Util/Util";
import axios from "axios";

export default class EditUser extends Component {
  state = {
    users: {}
  };
  componentDidMount() {
    let url = `http://localhost:8087/private/users?access_token=${getCookie(
      "Session"
    )}`;
    axios.get(url).then(res => {
      this.setState({users: res.data});
      console.log(res.data);
    });
  }

  render() {
    return (
      <div className="EditUser">
        <form>
          <img src={this.state.users.img}></img>
          <input id="email" value={this.state.users.email} type="email" placeholder="Email" />
          <input id="username" value={this.state.users.username} type="text" placeholder="Username" />
          <input id="password" type="text" placeholder="Password" />
          <input id="name" value={this.state.users.name} type="text" placeholder="Name" />
          <input id="surname" value={this.state.users.surname} type="text" placeholder="Surname" />
          <input id="phone" value={this.state.users.phone} type="text" placeholder="Phone" />
          <input id="address" value={this.state.users.address} type="text" placeholder="Address" />
        </form>
      </div>
    );
  }
}
