import React, { Component } from "react";
import "./Products.css";

export default class Products extends Component {
  state = {
    products: {}
  };
  async componentDidMount() {
    const response = await fetch (`http://localhost:8086/catalogs/categories/audio/subcategories/headphones`);
    const json = await response.json();
    this.setState({products: json});
  }

  render() {
    return (
      <div>
        <h2>Products</h2>
        <ul>

        </ul>
      </div>
    );
  }
}
