import React, { Component } from "react";
import "./Users.css";
import axios from "axios";

export default class Users extends Component {
  state = {
      users: []
  };
  getCookie(cname) {
    let name = cname + "=";
    let ca = document.cookie.split(";");
    for (let i = 0; i < ca.length; i++) {
      let c = ca[i];
      while (c.charAt(0) === " ") {
        c = c.substring(1);
      }
      if (c.indexOf(name) === 0) {
        return c.substring(name.length, c.length);
      }
    }
    return "";
  }
  componentDidMount() {
    let url = `http://localhost:8087/secure/users?access_token=${this.getCookie(
      "Session"
    )}`;
    axios.get(url).then(res => {
      this.setState({
        users: res.data
      });
    });
  }
  render() {
    return (
      <div className="Users">
        {this.state.users.map(user => (
          <div key={user.id}>{user.email} {user.name} {user.surname} {user.username}{}</div>
        ))}
      </div>
    );
  }
}
