import React, { Component } from "react";
import "./FullImage.css";
import Backdrop from "../Backdrop/Backdrop";

export default class FullImage extends Component {
  imagesSize = this.props.projectObj.images.length - 1;
  state = {
    currentImgIndex: this.props.imgIndex
  };

  render() {
    return (
      <React.Fragment>
        <div className="FullImage">
          <div className="button-left-fullscreen">
            <i
              className="fas fa-arrow-left arrow-fullscreen"
              onClick={() => {
                if (this.state.currentImgIndex === 0) {
                  this.setState({ currentImgIndex: this.imagesSize });
                } else {
                  this.setState((prevState, props) => {
                    return { currentImgIndex: prevState.currentImgIndex - 1 };
                  });
                }
              }}
            />
          </div>
          <div className="center-img full-image">
            <img
              src={require("../../../assets/" +
                this.props.imgFolder +
                "/" +
                this.props.projectObj.images[this.state.currentImgIndex])}
              alt={this.props.projectObj.images[this.state.currentImgIndex]}
            />
          </div>
          <div className="button-right-fullscreen">
            <i
              className="fas fa-arrow-right arrow-fullscreen"
              onClick={() => {
                if (this.state.currentImgIndex === this.imagesSize) {
                  this.setState({ currentImgIndex: 0 });
                } else {
                  this.setState((prevState, props) => {
                    return { currentImgIndex: prevState.currentImgIndex + 1 };
                  });
                }
              }}
            />
          </div>
        </div>
        <Backdrop
          {...this.props}
          currentImgIndex={this.state.currentImgIndex}
        />
      </React.Fragment>
    );
  }
}
