import React, { Component } from "react";
import "./Users.css";
import axios from "axios";

export default class Users extends Component {
  state = {
    users: []
  };
  getCookie(cname) {
    let name = cname + "=";
    let ca = document.cookie.split(";");
    for (let i = 0; i < ca.length; i++) {
      let c = ca[i];
      while (c.charAt(0) === " ") {
        c = c.substring(1);
      }
      if (c.indexOf(name) === 0) {
        return c.substring(name.length, c.length);
      }
    }
    return "";
  }
  componentDidMount() {
    let url = `http://localhost:8087/secure/users?access_token=${this.getCookie(
      "Session"
    )}`;
    axios.get(url).then(res => {
      console.log(res.data);

      let users = [];
      let array = res.data;
      for (let i = 0; i < array.length; i++) {
        users.push({ ...array[i], isOpen: false });
      }
      this.setState({
        users: users
      });
    });
  }
  render() {
    return (
      <div className="Users">
        {this.state.users.length === 0 ? null : (
          <table>
            <thead>
              <tr>
                <th>Username</th>
                <th>Name</th>
                <th>Email</th>
                <th>Role</th>
                <th>Enable</th>
              </tr>
            </thead>
            <tbody>
              {this.state.users.map((user, index) => (
                <React.Fragment key={user.id}>
                  <tr
                    onClick={() => {
                      let originalUsers = this.state.users;
                      originalUsers[index].isOpen = !originalUsers[index]
                        .isOpen;
                      this.setState({ users: originalUsers });
                    }}
                    className="hover-row"
                  >
                    <td>{user.username}</td>
                    <td>
                      {user.name} {user.surname}
                    </td>
                    <td>{user.email}</td>
                    <td>
                      {user.roles[0].name === "ROLE_USER" ? (
                        <p>User</p>
                      ) : user.roles[0].name === "ROLE_ADMIN" ? (
                        <p>Admin</p>
                      ) : null}
                    </td>
                    <td>
                      {user.enabled ? (
                        <div className="green" />
                      ) : (
                        <div className="red" />
                      )}
                    </td>
                  </tr>
                  {user.isOpen ? (
                    <tr>
                      <td colSpan="6">
                        <div className="more-info">
                          <div>
                            <img
                              src={user.img}
                              alt={user.username}
                              className="profile-img"
                            />
                          </div>
                          <div>Id: {user.id}</div>
                          <div>Phone: {user.phone}</div>
                          <div>Address: {user.address}</div>
                        </div>
                      </td>
                    </tr>
                  ) : null}
                </React.Fragment>
              ))}
            </tbody>
          </table>
        )}
      </div>
    );
  }
}
