import React, { Component } from "react";
import "./EditProduct.css";
import { connect } from "react-redux";
import { fetchProduct, removeProduct } from "../../store/actions/indexStore";

class EditProduct extends Component {
  componentDidMount() {
    window.scrollTo(0, 0)
    this.props.onfetchProduct(this.props.match.params.product);
  }
  render() {
    return (
      <div className="EditProduct">
        {this.props.match.params.product}

        <button
          className="btn-danger"
          onClick={() =>
            this.props.onremoveProduct(this.props.match.params.product)
          }
        >
          Delete Product
        </button>
      </div>
    );
  }
}
const mapStateToProps = state => {
  return {
    product: state.product
  };
};

const mapDispatchToProps = dispatch => {
  return {
    onfetchProduct: productId => dispatch(fetchProduct(productId)),
    onremoveProduct: productId => dispatch(removeProduct(productId))
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(EditProduct);
