import React, { Component } from "react";
import "./Items.css";
import Icons from "../../UI/Icons/Icons";
import Entry from "../../Entry/Entry";

export default class Items extends Component {
  state = {
    showEntry: false
  };
  render() {
    return (
      <div className="Items">
        <Icons type="bag" />
        <Icons
          type="user"
          onClick={() => {
            this.setState((prevState, props) => {
              return { showEntry: !prevState.showEntr };
            });
          }}
        />
        {this.state.showEntry ? <Entry /> : null}
      </div>
    );
  }
}
