import React, { Component } from "react";
import "./ProductPage";
import Spinner from "../UI/Spinner/Spinner";
import axios from "axios";

export default class ProductPage extends Component {
  state = {
    product: {
      attributes: {},
      images: []
    },
    loadData: false
  };
  componentDidMount() {
    axios
      .get(
        `http://localhost:8086/catalogs/categories/audio/subcategories/headphones/products/${
          this.props.match.params.id
        }`
      )
      .then(res => {
        console.log(res.data);
        this.setState({
          product: res.data,
          attributes: res.data.attributes,
          images: res.data.images,
          loadData: true
        });
      });
  }
  render() {
    let product = <Spinner />;
    if (this.state.loadData) {
      product = (
        <React.Fragment>
          <h1>
            {this.state.product.attributes.brand +
              " " +
              this.state.product.name}
          </h1>
          {this.state.product.images.map(img => (
            <img style={{width: "150px"}} key={img.url} src={img.url} alt={img.url} />
          ))}
          <p>{this.state.product.price} â‚¬</p>
          <p>{this.state.product.quantity} units</p>
          <p>{this.state.product.description}</p>
        </React.Fragment>
      );
    }
    return <div>{product}</div>;
  }
}
