import React, { Component } from "react";
import "./Users.css";
//import axios from "axios";
//import { getCookie } from "../Util/Util";
import Switch from "../UI/Switch/Switch";

import { connect } from "react-redux";
import { addUser, removeUser, fetchUsers } from "../../store/actions/index";

class Users extends Component {
  componentDidMount() {
    this.props.onfetchUsers();
  }
  /*changeRoleUserHandler(role, user, index) {
    let id;
    if (role === "Admin") {
      id = 2;
    } else if (role === "User") {
      id = 1;
    }
    let r = "ROLE_" + role.toUpperCase();
    user.roles[0].id = id;
    user.roles[0].name = r;
    axios
      .put(
        `http://localhost:8087/secure/users/${user.id}?access_token=${getCookie(
          "Session"
        )}`,
        user
      )
      .then(res => {
        if (res.status === 200) {
          let userArr = this.state.users;
          userArr[index] = user;
          this.setState({ users: userArr });
        }
      });
  }
  changeEnableUserHandler(enable, user, index) {
    user.enabled = enable;
    user.accountNonExpired = enable;
    user.accountNonLocked = enable;
    user.account_non_expired = enable;
    user.account_non_locked = enable;
    user.credentialsNonExpired = enable;
    axios
      .put(
        `http://localhost:8087/secure/users/${user.id}?access_token=${getCookie(
          "Session"
        )}`,
        user
      )
      .then(res => {
        if (res.status === 200) {
          let userArr = this.state.users;
          userArr[index] = user;
          this.setState({ users: userArr });
        }
      });
  }*/
  render() {
    return (
      <div className="Users">
        {this.props.user.user.length === 0 ? null : (
          <table>
            <thead>
              <tr>
                <th>Username</th>
                <th>Name</th>
                <th>Email</th>
                <th>Role</th>
                <th>Enable</th>
              </tr>
            </thead>
            <tbody>
              {this.props.user.user.map((user, index) => (
                <React.Fragment key={user.id}>
                  <tr className="hover-row">
                    <td>{user.username}</td>
                    <td>
                      {user.name} {user.surname}
                    </td>
                    <td>{user.email}</td>
                    <td>
                      <select
                        value={user.roles[0].name}
                        onChange={e => {
                          this.changeRoleUserHandler(
                            e.target.options[e.target.selectedIndex].text,
                            user,
                            index
                          );
                        }}
                      >
                        <option value="ROLE_USER">User</option>
                        <option value="ROLE_ADMIN">Admin</option>
                      </select>
                    </td>
                    <td>
                      <Switch />
                      {user.enabled ? (
                        <div
                          className="green"
                          onClick={() =>
                            this.changeEnableUserHandler(0, user, index)
                          }
                        />
                      ) : (
                        <div
                          className="red"
                          onClick={() =>
                            this.changeEnableUserHandler(1, user, index)
                          }
                        />
                      )}
                    </td>
                    <td
                      className="dropdown-btn-wrapper"
                      onClick={() => {
                        let originalUsers = this.props.users;
                        originalUsers[index].isOpen = !originalUsers[index]
                          .isOpen;
                        this.setState({ users: originalUsers });
                      }}
                    >
                      <i className="fas fa-caret-down dropdown-btn" />
                    </td>
                  </tr>
                  {user.isOpen ? (
                    <tr>
                      <td colSpan="6">
                        <div className="more-info">
                          <div>
                            <img
                              src={user.img}
                              alt={user.username}
                              className="profile-img"
                            />
                          </div>
                          <div className="info">
                            <div className="id">
                              <span className="bold">Id:</span> {user.id}
                            </div>
                            <div className="phone">
                              <span className="bold">Phone:</span> {user.phone}
                            </div>
                            <div className="address">
                              <span className="bold">Address:</span>{" "}
                              {user.address}
                            </div>
                          </div>
                        </div>
                      </td>
                    </tr>
                  ) : null}
                </React.Fragment>
              ))}
            </tbody>
          </table>
        )}
      </div>
    );
  }
}

const mapStateToProps = state => {

  return {
    users: state.users
  };
};

const mapDispatchToProps = dispatch => {
  return {
    onfetchUsers: () => dispatch(fetchUsers()),
    onUserAdded: user => dispatch(addUser(user)),
    onUserRemoved: username => dispatch(removeUser(username))
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Users);
