import React, { Component } from "react";
import "./Categorization.css";

export default class Categorization extends Component {
  state = {
    category: "",
    subcategory: "",
    brand: "",
    weight: "",
    size: "",
    color: "",
    isOpenCategory: false,
    isOpenSubcategory: false,
    isOpenBrand: false,
    isOpenWeight: false,
    isOpenSize: false,
    isOpenColor: false
  }
  postCategoryHandler() {
    console.log(this.state.category);
    this.setState({ category: "" });
  }
  postSubcategoryHandler() {
    console.log(this.state.subcategory);
    this.setState({ subcategory: "" });
  }
  postBrandHandler() {
    console.log(this.state.brand);
    this.setState({ brand: "" });
  }
  postWeightHandler() {
    console.log(this.state.weight);
    this.setState({ weight: "" });
  }
  postColorHandler() {
    console.log(this.state.color);
    this.setState({ color: "" });
  }
  render() {
    return (
      <div className="Categorization">
        <table>
          <tbody>
            <tr className="hover-row"
              onClick={() => this.setState((prevState, props) => {
                return { isOpenCategory: !prevState.isOpenCategory };
              })}>
              <td>Category</td>
              <td
                className="dropdown-btn-wrapper"
              >
                <i className="fas fa-caret-down dropdown-btn" />
              </td>
            </tr>
            {this.state.isOpenCategory ? (
              <tr>
                <td colSpan="2">
                  <div className="more-info">
                    <input
                      onChange={e => {
                        this.setState({
                          category: e.target.value
                        }
                        );
                      }}
                      value={this.state.category}
                      type="text"
                      placeholder="Category"
                    />
                  </div>
                  <button onClick={() => this.postCategoryHandler()}>Submit</button>
                  <button onClick={() => this.setState({ category: "" })
                  }>Reset</button>
                </td>
              </tr>
            ) : null}
            <tr className="hover-row"
              onClick={() => this.setState((prevState, props) => {
                return { isOpenSubcategory: !prevState.isOpenSubcategory };
              })}>
              <td>Subcategory</td>
              <td
                className="dropdown-btn-wrapper"
              >
                <i className="fas fa-caret-down dropdown-btn" />
              </td>
            </tr>
            {this.state.isOpenSubcategory ? (
              <tr>
                <td colSpan="2">
                  <div className="more-info">
                    <input
                      onChange={e => {
                        this.setState({
                          subcategory: e.target.value
                        }
                        );
                      }}
                      value={this.state.subcategory}
                      type="text"
                      placeholder="Subcategory"
                    />
                  </div>
                  <button onClick={() => this.postSubcategoryHandler()}>Submit</button>
                  <button onClick={() => this.setState({ subcategory: "" })
                  }>Reset</button>
                </td>
              </tr>
            ) : null}
            <tr className="hover-row"
              onClick={() => this.setState((prevState, props) => {
                return { isOpenBrand: !prevState.isOpenBrand };
              })}>
              <td>Brand</td>
              <td
                className="dropdown-btn-wrapper"
              >
                <i className="fas fa-caret-down dropdown-btn" />
              </td>
            </tr>
            {this.state.isOpenBrand ? (
              <tr>
                <td colSpan="2">
                  <div className="more-info">
                    <input
                      onChange={e => {
                        this.setState({
                          brand: e.target.value
                        }
                        );
                      }}
                      value={this.state.brand}
                      type="text"
                      placeholder="Brand"
                    />
                  </div>
                  <button onClick={() => this.postBrandHandler()}>Submit</button>
                  <button onClick={() => this.setState({ brand: "" })
                  }>Reset</button>
                </td>
              </tr>
            ) : null}

            <tr className="hover-row"
              onClick={() => this.setState((prevState, props) => {
                return { isOpenWeight: !prevState.isOpenWeight };
              })}>
              <td>Weight</td>
              <td
                className="dropdown-btn-wrapper"
              >
                <i className="fas fa-caret-down dropdown-btn" />
              </td>
            </tr>
            {this.state.isOpenWeight ? (
              <tr>
                <td colSpan="2">
                  <div className="more-info">
                    <input
                      onChange={e => {
                        this.setState({
                          weight: e.target.value
                        }
                        );
                      }}
                      value={this.state.weight}
                      type="text"
                      placeholder="Weight"
                    />
                  </div>
                  <button onClick={() => this.postWeightHandler()}>Submit</button>
                  <button onClick={() => this.setState({ weight: "" })
                  }>Reset</button>
                </td>
              </tr>
            ) : null}
            <tr className="hover-row"
              onClick={() => this.setState((prevState, props) => {
                return { isOpenSize: !prevState.isOpenSize };
              })}>
              <td>Size</td>
              <td
                className="dropdown-btn-wrapper"
              >
                <i className="fas fa-caret-down dropdown-btn" />
              </td>
            </tr>
            {this.state.isOpenSize ? (
              <tr>
                <td colSpan="2">
                  <div className="more-info">
                    <input
                      onChange={e => {
                        this.setState({
                          size: e.target.value
                        }
                        );
                      }}
                      value={this.state.size}
                      type="text"
                      placeholder="Size"
                    />
                  </div>
                  <button onClick={() => this.postSizetHandler()}>Submit</button>
                  <button onClick={() => this.setState({ size: "" })
                  }>Reset</button>
                </td>
              </tr>
            ) : null}
            <tr className="hover-row"
              onClick={() => this.setState((prevState, props) => {
                return { isOpenColor: !prevState.isOpenColor };
              })}>
              <td>Color</td>
              <td
                className="dropdown-btn-wrapper"
              >
                <i className="fas fa-caret-down dropdown-btn" />
              </td>
            </tr>
            {this.state.isOpenColor ? (
              <tr>
                <td colSpan="2">
                  <div className="more-info">
                    <input
                      onChange={e => {
                        this.setState({
                          color: e.target.value
                        }
                        );
                      }}
                      value={this.state.color}
                      type="text"
                      placeholder="Color"
                    />
                  </div>
                  <button onClick={() => this.postColortHandler()}>Submit</button>
                  <button onClick={() => this.setState({ color: "" })
                  }>Reset</button>
                </td>
              </tr>
            ) : null}
          </tbody>
        </table>
      </div>
    );
  }
}
