import React, { Component } from "react";
import "./Categorization.css";

export default class Categorization extends Component {
  state = {
    category: "",
    subcategory: "",
    brands: "",
    weight: "",
    size: "",
    color: "",
    isOpenCategory: false,
    isOpenSubcategory: false,
    isOpenBrands: false,
    isOpenWeight: false,
    isOpenSize: false,
    isOpenColor: false
  }
  postCategoryHandler() {
    console.log(this.state.category);
    this.setState({ category: "" });
  }
  render() {
    return (
      <div className="Categorization">
        <table>
          <tbody>
            <tr className="hover-row"
              onClick={() => this.setState((prevState, props) => {
                return { isOpenCategory: !prevState.isOpenCategory };
              })}>
              <td>Category</td>
              <td
                className="dropdown-btn-wrapper"
              >
                <i className="fas fa-caret-down dropdown-btn" />
              </td>
            </tr>
            {this.state.isOpenCategory ? (
              <tr>
                <td colSpan="2">
                  <div className="more-info">
                    <input
                      onChange={e => {
                        this.setState({
                          category: e.target.value
                        }
                        );
                      }}
                      value={this.state.category}
                      type="text"
                      placeholder="Category"
                    />
                  </div>
                  <button onClick={() => this.postCategoryHandler()}>Submit</button>
                  <button onClick={() => this.setState({ category: "" })
                  }>Reset</button>
                </td>
              </tr>
            ) : null}
          </tbody>
        </table>
      </div>
    );
  }
}
