import React, { Component } from "react";
import "./EditUser.css";
import { getCookie } from "../Util/Util";
import axios from "axios";

export default class EditUser extends Component {
  state = {
    user: {
      email: "",
      name: "",
      surname: "",
      phone: "",
      address: "",
      username: "",
      password: "",
      img: ""
    }
  };
  componentDidMount() {
    let url = `http://localhost:8087/private/users?access_token=${getCookie(
      "Session"
    )}`;
    axios.get(url).then(res => {
      this.setState({ user: res.data });
    });
  }

  render() {
    return (
      <div className="EditUser">
        <div className="form-img">
          <img
            src={this.state.user.img}
            alt={this.state.user.username}
            className="profile-img"
          />
        </div>
        <div className="form-fields">
          <input
            id="email"
            value={this.state.user.email}
            onChange={e => {
              this.setState({
                user: { ...this.state.user, email: e.target.value }
              });
            }}
            type="email"
            placeholder="Email"
          />
          <input
            id="name"
            onChange={e => {
              this.setState({
                user: { ...this.state.user, name: e.target.value }
              });
            }}
            value={this.state.user.name}
            type="text"
            placeholder="Name"
          />
          <input
            id="surname"
            onChange={e => {
              this.setState({
                user: { ...this.state.user, surname: e.target.value }
              });
            }}
            value={this.state.user.surname}
            type="text"
            placeholder="Surname"
          />
          <input
            id="phone"
            onChange={e => {
              this.setState({
                user: { ...this.state.user, phone: e.target.value }
              });
            }}
            value={this.state.user.phone}
            type="text"
            placeholder="Phone"
          />
          <input
            id="address"
            onChange={e => {
              this.setState({
                user: { ...this.state.user, address: e.target.value }
              });
            }}
            value={this.state.user.address}
            type="text"
            placeholder="Address"
          />
          <input
            id="username"
            value={this.state.user.username}
            onChange={e => {
              this.setState({
                user: { ...this.state.user, username: e.target.value }
              });
            }}
            type="text"
            placeholder="Username"
          />
          <input id="password" type="text" placeholder="Password" />
        </div>

        <button>Save</button>
        <button>Cancel</button>
      </div>
    );
  }
}
