import React from "react";
import "./Modal.css";
import Backdrop from "../Backdrop/Backdrop";

export default function Modal() {
  let value = "";
  return (
    <React.Fragment>
      <div className="Modal">
        <h2>Change Profile Image</h2>
        <input
        onChange={e => {
          value = e.target.value;
        }}
        type="text" placeholder="Image URL" />
      </div>
      <Backdrop value={value} />
    </React.Fragment>
  );
}
