import React, { Component } from "react";
import "./Items.css";
import Icons from "../../UI/Icons/Icons";
import Entry from "../../Entry/Entry";
import Backdrop from "../../UI/Backdrop/Backdrop";
import UserMenu from "../../UserMenu/UserMenu";
import axios from "axios";
import { getCookie } from "../../Util/Util";

export default class Items extends Component {
  state = {
    user: {
      roles: [
        {
          name: ""
        }
      ]
    },
    showEntry: false
  };
  closeFullScreenHandler = (index = null) => {
    this.setState({
      showEntry: false
    });
  };
  componentDidMount() {
    let url = `http://localhost:8087/private/users?access_token=${getCookie(
      "Session"
    )}`;
    axios.get(url).then(res => {
      this.setState({ user: res.data });
    });
  }
  render() {
    let userModel = (
      <React.Fragment>
        <UserMenu
          closeFullscreen={() => this.closeFullScreenHandler()}
          role={this.state.user.roles[0].name}
          name={this.state.user.name + " " + this.state.user.surname}
        />
        <Backdrop
          isTransparent={true}
          closeFullscreen={() => this.closeFullScreenHandler()}
        />
      </React.Fragment>
    );
    if (getCookie("Session") === "") {
      userModel = (
        <React.Fragment>
          <Entry closeEntry={() => this.closeFullScreenHandler()} />
          <Backdrop closeFullscreen={index => this.closeFullScreenHandler()} />
        </React.Fragment>
      );
    }

    return (
      <div className="Items">
        <Icons type="bag" classNames="icon" />
        <div
          onClick={() => {
            this.setState((prevState, props) => {
              return { showEntry: !prevState.showEntry };
            });
          }}
        >
          {getCookie("Session") !== "" ? (
            <img
              alt={this.state.user.username}
              src={this.state.user.img}
              className="small-img"
            />
          ) : (
            <Icons type="user" classNames="icon" />
          )}
        </div>
        {this.state.showEntry ? userModel : null}
      </div>
    );
  }
}
