import React, { Component } from "react";
import "./EditProduct.css";
import { connect } from "react-redux";
import { removeProduct } from "../../store/actions/indexStore";
import TransitionGroup from "react-transition-group/TransitionGroup";
import CSSTransition from "react-transition-group/CSSTransition";
import axios from "axios";

class EditProduct extends Component {
  state = {
    product: {
      name: "",
      description: "",
      price: 0,
      quantity: 0,
      thumbnail: ""
    },
    attributes: {
      brand: "",
      color: "",
      weight: "",
      size: ""
    },
    imageUrl: "",
    imageArr: [],
    imageArrOriginal: [],
    errorAddImage: "",
    errorAddImageFlash: false,
    error: ""
  };
  componentDidMount() {
    window.scrollTo(0, 0);
    this.fetchProductHandler(this.props.match.params.product);
  }
  updateProductHandler(
    productId,
    product,
    attributes,
    imageArr,
    imageArrOriginal
  ) {
    const imagesArrAdd = [];
    const imagesArrNotDeleted = [];
    for (let i = 0; i < imageArr.length; i++) {
      if (imageArr[i].key === "") {
        imagesArrAdd.push(imageArr[i].url);
      } else {
        imagesArrNotDeleted.push(imageArr[i]);
      }
    }
    const imageArrDeleted = imageArrOriginal.filter(
      v => imagesArrNotDeleted.indexOf(v) === -1
    );
    const imageArrDeletedUrls = [];
    for (let d = 0; d < imageArrDeleted.length; d++) {
      imageArrDeletedUrls.push(imageArrDeleted[d].url);
    }
    //http://localhost:8086/catalogs/categories/categories/subcategories/subcategories/products/${productId}/images/${imageArrDeletedUrls[]}
    console.log(imageArrDeletedUrls);
  }

  fetchProductHandler(productId) {
    let urlProduct = `http://localhost:8086/catalogs/categories/audio/subcategories/headphones/products/${productId}`;
    axios.get(urlProduct).then(res => {
      this.setState({ product: res.data });
    });
    let urlImages = `http://localhost:8086/catalogs/categories/audio/subcategories/headphones/products/${productId}/images`;
    axios.get(urlImages).then(res => {
      let imageArr = [];
      for (let i = 0; i < res.data.length; i++) {
        imageArr.push(res.data[i].url);
      }
      this.setState({ imageArr: res.data, imageArrOriginal: res.data });
    });
  }
  addUrlHandler() {
    if (this.state.imageUrl !== "") {
      if (this.state.imageArr.indexOf(this.state.imageUrl) === -1) {
        this.state.imageArr.push({ key: "", url: this.state.imageUrl });
        this.setState({ errorAddImage: "", imageUrl: "" });
      } else {
        this.setState({
          errorAddImage: "Duplicate Urls.",
          errorAddImageFlash: true
        });
      }
    } else {
      this.setState({
        errorAddImage: "Invalid Url.",
        errorAddImageFlash: true
      });
    }
  }
  render() {
    return (
      <div className="EditProduct">
        <div className="product-wrapper">
          <div className="image">
            <img
              alt={this.state.product.name}
              src={this.state.product.thumbnail}
            />
          </div>
          <div className="thumbnail">
            <p className="label">Thumbnail URL:</p>
            <input
              value={this.state.product.thumbnail}
              type="text"
              placeholder="Thumbnail"
              onChange={e =>
                this.setState({
                  product: {
                    ...this.state.product,
                    thumbnail: e.target.value
                  }
                })
              }
            />
          </div>
          <div className="name">
            <p className="label">Name:</p>
            <input
              value={this.state.product.name}
              type="text"
              placeholder="Name"
              onChange={e =>
                this.setState({
                  product: {
                    ...this.state.product,
                    name: e.target.value
                  }
                })
              }
            />
          </div>
          <div className="price">
            <p className="label">Price:</p>
            <input
              value={this.state.product.price}
              type="number"
              placeholder="Price"
              onChange={e =>
                this.setState({
                  product: {
                    ...this.state.product,
                    price: e.target.value
                  }
                })
              }
            />
          </div>
          <div className="quantity">
            <p className="label">Quantity:</p>
            <input
              value={this.state.product.quantity}
              type="number"
              placeholder="Quantity"
              onChange={e =>
                this.setState({
                  product: {
                    ...this.state.product,
                    quantity: e.target.value
                  }
                })
              }
            />
          </div>
          <div className="attributes">
            <div className="brand">
              <p className="label">Brand:</p>
              <select
                value={this.state.attributes.brand}
                onChange={e =>
                  this.setState({
                    attributes: {
                      ...this.state.attributes,
                      brand: e.target.options[e.target.selectedIndex].text
                    }
                  })
                }
              >
                <option value="">All Brands</option>
                <option value="Bose">Bose</option>
                <option value="Audio-Technica">Audio-Technica</option>
                <option value="Sennheiser">Sennheiser</option>
                <option value="Huawei">Huawei</option>
              </select>
            </div>
            <div className="color">
              <p className="label">Color:</p>
              <select
                onChange={e =>
                  this.setState({
                    attributes: {
                      ...this.state.attributes,
                      color: e.target.options[e.target.selectedIndex].text
                    }
                  })
                }
              >
                <option value="">All Colors</option>
                <option value="black">Black</option>
                <option value="gray">Gray</option>
                <option value="blue">Blue</option>
                <option value="green">Green</option>
                <option value="purple">Purple</option>
                <option value="pink">Pink</option>
                <option value="yellow">Yellow</option>
                <option value="orange">Orange</option>
                <option value="red">Red</option>
                <option value="brown">Brown</option>
                <option value="white">White</option>
              </select>
            </div>
            <div className="weight">
              <p className="label">Weight:</p>
              <select
                onChange={e =>
                  this.setState({
                    attributes: {
                      ...this.state.attributes,
                      weight: e.target.options[e.target.selectedIndex].text
                    }
                  })
                }
              >
                <option value="">All Weights</option>
                <option value="0kg-1kg">0kg - 1kg</option>
                <option value="1kg-2kg">1kg - 2kg</option>
                <option value="2kg-3kg">2kg - 3kg</option>
                <option value="3kg-4kg">3kg - 4kg</option>
                <option value="4kg-5kg">4kg - 5kg</option>
              </select>
            </div>
            <div className="size">
              <p className="label">Size:</p>
              <select
                onChange={e =>
                  this.setState({
                    attributes: {
                      ...this.state.attributes,
                      size: e.target.options[e.target.selectedIndex].text
                    }
                  })
                }
              >
                <option value="">All Sizes</option>
                <option value="small">Small</option>
                <option value="medium">Medium</option>
                <option value="big">Big</option>
              </select>
            </div>
          </div>
          <div className="description">
            <p className="label">Description:</p>
            <textarea
              type="text"
              placeholder="Description"
              value={this.state.product.description}
              onChange={e =>
                this.setState({
                  product: {
                    ...this.state.product,
                    description: e.target.value
                  }
                })
              }
            />
          </div>
          <div className="images">
            <p className="label">Image Url:</p>
            <div className="image-wrapper">
              <input
                onChange={e => this.setState({ imageUrl: e.target.value })}
                value={this.state.imageUrl}
                type="text"
                placeholder="Image URL"
                onKeyPress={e => {
                  if (e.key === "Enter") {
                    this.addUrlHandler();
                  }
                }}
              />
              <i
                onClick={() => {
                  this.addUrlHandler();
                }}
                className="fas fa-plus"
              />
            </div>
            <p
              style={{
                visibility:
                  this.state.errorAddImage !== "" ? "visible" : "hidden"
              }}
              onAnimationEnd={() =>
                this.setState({ errorAddImageFlash: false })
              }
              className={
                this.state.errorAddImageFlash ? "error flash" : "error"
              }
            >
              {this.state.errorAddImage}
            </p>
          </div>
          <TransitionGroup component="ul" className="image-list">
            {this.state.imageArr.map(image => (
              <CSSTransition key={image.url} classNames="fade" timeout={400}>
                <li className="image-url">
                  <div className="image">
                    <img src={image.url} alt={image.url} />
                  </div>
                  {image.url}
                  <i
                    className="fas fa-times"
                    onClick={() => {
                      let imageArr = [...this.state.imageArr];
                      for (let i = 0; i < imageArr.length; i++) {
                        if (imageArr[i].url === image.url) {
                          imageArr.splice(i, 1);
                        }
                      }
                      this.setState({ imageArr: imageArr });
                    }}
                  />
                </li>
              </CSSTransition>
            ))}
          </TransitionGroup>
          <div className="buttons">
            <button
              className="submit"
              onClick={() => {
                this.updateProductHandler(
                  this.props.match.params.product,
                  this.state.product,
                  this.state.attributes,
                  this.state.imageArr,
                  this.state.imageArrOriginal
                );
              }}
            >
              Submit
            </button>
            <button
              className="reset"
              onClick={() =>
                this.fetchProductHandler(this.props.match.params.product)
              }
            >
              Reset
            </button>
            <button
              className="cancel"
              onClick={() => this.props.history.push({ pathname: "/" })}
            >
              Cancel
            </button>
            <button
              className="btn-danger"
              onClick={() =>
                this.props.onremoveProduct(this.props.match.params.product)
              }
            >
              Delete Product
            </button>
          </div>
        </div>
      </div>
    );
  }
}
const mapStateToProps = state => {
  return {
    catalog: state.catalog,
    product: state.product
  };
};

const mapDispatchToProps = dispatch => {
  return {
    onremoveProduct: productId => dispatch(removeProduct(productId))
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(EditProduct);
