import React, { Component } from "react";
import "./Items.css";
import Icons from "../../UI/Icons/Icons";
import Entry from "../../Entry/Entry";
import Backdrop from "../../UI/Backdrop/Backdrop";
import UserMenu from "../../UserMenu/UserMenu";
import axios from "axios";
import { getCookie } from "../../Util/Util";
import { withRouter } from "react-router-dom";
import { connect } from "react-redux";

class Items extends Component {
  state = {
    user: {
      roles: [
        {
          name: ""
        }
      ]
    },
    showEntry: false,
    quantityCart: 0
  };
  closeFullScreenHandler = (index = null) => {
    this.setState({
      showEntry: false
    });
  };
  componentDidMount() {
    const quantityCart = this.quantityCart();
    this.setState({ quantityCart: quantityCart });
    let url = `http://localhost:8087/private/users?access_token=${getCookie(
      "Session"
    )}`;
    axios.get(url).then(res => {
      this.setState({ user: res.data });
    });
  }
  quantityCart() {
    if (
      localStorage.getItem("quantity") !== null &&
      localStorage.getItem("quantity") !== ""
    ) {
      const quantityLs = localStorage.getItem("quantity").split(",");
      let total = 0;
      for (let q = 0; q < quantityLs.length; q++) {
        total += parseInt(quantityLs[q], 10);
      }
      return total;
    }
    return 0;
  }
  render() {
    let userModel = (
      <React.Fragment>
        <UserMenu
          closeFullscreen={() => this.closeFullScreenHandler()}
          role={this.state.user.roles[0].name}
          name={this.state.user.name + " " + this.state.user.surname}
        />
        <Backdrop
          isTransparent={true}
          closeFullscreen={() => this.closeFullScreenHandler()}
        />
      </React.Fragment>
    );
    if (getCookie("Session") === "") {
      userModel = (
        <React.Fragment>
          <Entry closeEntry={() => this.closeFullScreenHandler()} />
          <Backdrop closeFullscreen={index => this.closeFullScreenHandler()} />
        </React.Fragment>
      );
    }
    return (
      <div className="Items">
        <div
          onClick={() => this.props.history.push({ pathname: "/orders" })}
          className="bag-wrapper"
        >
          <Icons type="bag" classNames="icon" />
          <div
            className="quantity-number"
            style={{
              display: this.state.quantityCart === 0 ? "none" : "block"
            }}
          >
            {this.state.quantityCart}
          </div>
        </div>
        <div
          onClick={() => {
            this.setState((prevState, props) => {
              return { showEntry: !prevState.showEntry };
            });
          }}
        >
          {getCookie("Session") !== "" ? (
            <img
              alt={this.props.user.authenticatedUser.username}
              src={this.props.user.authenticatedUser.img}
              className="small-img"
            />
          ) : (
            <Icons type="user" classNames="icon" />
          )}
        </div>
        {this.state.showEntry ? userModel : null}
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    user: state.user
  };
};

export default connect(mapStateToProps)(withRouter(Items));
