import * as actionTypes from "../actions/actionTypes";

const initialState = {
  allUsers: [],
  authenticatedUser: {}
};

const addUser = (state, action) => {
  return [];
};
const removeUser = (state, action) => {
  return [];
};
const fetchUsers = (state, action) => {
  return { ...state, allUsers: [...action.users] };
};

const updateEnable = (state, action) => {
  console.log(action.index);
  let allUsers = [...action.users];
  allUsers[action.index].enable = !state.allUsers[action.index].enable;
  allUsers[action.index].enabled = !state.allUsers[action.index].enabled;
  allUsers[action.index].accountNonExpired = !state.allUsers[action.index]
    .accountNonExpired;
  allUsers[action.index].accountNonLocked = !state.allUsers[action.index]
    .accountNonLocked;
  allUsers[action.index].account_non_expired = !state.allUsers[action.index]
    .account_non_expired;
  allUsers[action.index].account_non_locked = !state.allUsers[action.index]
    .account_non_locked;
  allUsers[action.index].credentialsNonExpired = !state.allUsers[action.index]
    .credentialsNonExpired;
  return { ...state, allUsers: allUsers };
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case actionTypes.ADD_USER:
      return addUser(state, action);
    case actionTypes.REMOVE_USER:
      return removeUser(state, action);
    case actionTypes.FETCH_USERS:
      return fetchUsers(state, action);
    case actionTypes.UPDATE_ENABLE:
      return updateEnable(state, action);
    default:
      return state;
  }
};

export default reducer;
