import React, { Component } from "react";
import "./Items.css";
import Icons from "../../UI/Icons/Icons";
import Entry from "../../Entry/Entry";
import Backdrop from "../../UI/Backdrop/Backdrop";
import UserMenu from "../../UserMenu/UserMenu";
import { getCookie } from "../../Util/Util";
import { withRouter } from "react-router-dom";
import { connect } from "react-redux";
import { fetchAuthenticatedUser, fetchProductOrder, resetError } from "../../../store/actions/indexStore";

class Items extends Component {
  state = {
    showEntry: false,
    quantityCart: 0
  };
  closeFullScreenHandler = (index = null) => {
    this.setState({
      showEntry: false
    });
  };
  componentDidMount() {
    this.props.onfetchProductOrder();
    this.props.onfetchAuthenticatedUser();
    const quantityCart = this.quantityCart();
    this.setState({ quantityCart: quantityCart });
  }
  quantityCart() {
    if (
      localStorage.getItem("quantity") !== null &&
      localStorage.getItem("quantity") !== ""
    ) {
      const quantityLs = localStorage.getItem("quantity").split(",");
      let total = 0;
      for (let q = 0; q < quantityLs.length; q++) {
        total += parseInt(quantityLs[q], 10);
      }
      return total;
    }
    return 0;
  }
  render() {
    return (
      <div className="Items">
        <div
          onClick={() => this.props.history.push({ pathname: "/orders" })}
          className="bag-wrapper"
        >
          <Icons type="bag" classNames="icon" />
          <div
            className="quantity-number"
            style={{
              display: this.props.order.total === 0 ? "none" : "block"
            }}
          >
            {this.props.order.total}
          </div>
        </div>
        <div
          onClick={() => {
            this.setState((prevState, props) => {
              return { showEntry: !prevState.showEntry };
            });
          }}
        >
          {getCookie("Session") !== "" ? (
            <img
              alt={this.props.user.authenticatedUser.username}
              src={this.props.user.authenticatedUser.img}
              className="small-img"
            />
          ) : (
            <Icons type="user" classNames="icon" />
          )}
        </div>
        {this.state.showEntry ? (
          getCookie("Session") === "" ? (
            <React.Fragment>
              <Entry closeEntry={() => this.closeFullScreenHandler()} />
              <div onClick={() => this.props.onresetError}>
              <Backdrop
                closeFullscreen={index => this.closeFullScreenHandler()}
                
              />
              </div>
            </React.Fragment>
          ) : (
            <React.Fragment>
              <UserMenu
                closeFullscreen={() => this.closeFullScreenHandler()}
                role={this.props.user.authenticatedUser.roles[0].name}
                name={
                  this.props.user.authenticatedUser.name +
                  " " +
                  this.props.user.authenticatedUser.surname
                }
              />
              <Backdrop
                isTransparent={true}
                closeFullscreen={() => this.closeFullScreenHandler()}
              />
            </React.Fragment>
          )
        ) : null}
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    user: state.user,
    order: state.order
  };
};

const mapDispatchToProps = dispatch => {
  return {
    onfetchProductOrder: () => dispatch(fetchProductOrder()),
    onfetchAuthenticatedUser: () => dispatch(fetchAuthenticatedUser()),
    onresetError: () => dispatch(resetError())
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(withRouter(Items));
