import React, { Component } from "react";
import Toolbar from "../Toolbar/Toolbar";
import NotFound from "../NotFound/NotFound";
import Products from "../Products/Products";
import { Route, Switch } from "react-router-dom";
import ProductPage from "../ProductPage/ProductPage";
import Users from "../Users/Users";
import EditUser from "../EditUser/EditUser";
import CreateProduct from "../CreateProduct/CreateProduct";
import Categorization from "../Categorization/Categorization";
import ManageOrders from "../ManageOrders/ManageOrders";
import Orders from "../Orders/Orders";
import PrivateRoute from "../PrivateRoute/PrivateRoute";

export default class Layout extends Component {
  state = {
    token: ""
  };
  render() {
    return (
      <React.Fragment>
        <Route path="/" component={Toolbar} />
        <Switch>
          <Route path="/user" component={EditUser} />
          <Route path="/orders" component={Orders} />
          <PrivateRoute path="/secure/users" component={Users} />
          <PrivateRoute path="/secure/categorization" component={Categorization} />
          <PrivateRoute path="/secure/orders" component={ManageOrders} />
          <PrivateRoute path="/secure/products" component={ManageOrders} />
          <Route
            path="/:categories/:subcategories/:id"
            component={ProductPage}
          />
          <Route path="/:categories/:subcategories" component={Products} />
          <Route component={NotFound} />
        </Switch>
      </React.Fragment>
    );
  }
}
