                            classNames="fade"
                            timeout={400}
                          >
                            <li className="list-li">
                              {category.name}
                              <i
                                className="fas fa-times btn-icon"
                                onClick={() => {
                                  this.props.onremoveCategory(category.id);
                                }}
                              />
