                          this.setState({
                            selectedCategory:
                              e.target.options[e.target.selectedIndex].text
                          })
                        }
                      >
                        {this.props.catalog.catalog.map(category => (
                          <option key={category.id} value={category.name}>
                            {category.name}
                          </option>
                        ))}
                      </select>
                      <p className="input-info">Subcategory:</p>
                      <input
                        onChange={e => {
                          this.setState({
                            subcategory: e.target.value
                          });
                        }}
                        value={this.state.subcategory}
                        type="text"
                        placeholder="Subcategory"
                      />
                      <div className="buttons">
                        <button
                          className="submit"
                          onClick={() => this.postSubcategoryHandler()}
                        >
                          Submit
                        </button>
                        <button
                          className="reset"
                          onClick={() => this.setState({ subcategory: "" })}
                        >
                          Reset
                        </button>
                      </div>

                      <ul>
                        {this.props.catalog.catalog.map(category => {
                          if(this.state.selectedCategory === "") {
                            return category.subcategories.map(subcategory => (
                              <li key={subcategory.id}>{subcategory.name}</li>
                            ));
                          }
                          if (category.name === this.state.selectedCategory) {
                            return category.subcategories.map(subcategory => (
                              <li key={subcategory.id}>{subcategory.name}</li>
                            ));
                          }
                          return null;
                        })}
                      </ul>

                      {/*
                      
                          <React.Fragment  key={category.id}>

                          <li className="list-li">
                          {category.name}
                          <i
                            className="fas fa-caret-down btn-icon"
                            onClick={() => {
                              if (this.state[category.id] === undefined) {
