import React, { Component } from "react";
import "./Orders.css";
import axios from "axios";

export default class Orders extends Component {
  state = {
    products: []
  }
  componentDidMount() {
    let products = localStorage.getItem("product").split(",");
    axios({
      method: "post",
      headers: { "content-type": "application/json" },
      data: products,
      url: "http://localhost:8086/products/arr"
    })
      .then(res => {
        console.log(res.data);

        this.setState({ products: res.data });
      });

  }
  render() {
    return <div className="Orders">
      <div className="cart-products">
        <div className="cart-products-wrapper">
          <table>
            <thead>
              <tr>
                <th>Item</th>
                <th>Description</th>
                <th>Price</th>
                <th>Quantity</th>
                <th>Subtotal</th>
              </tr>
            </thead>
            <tbody>
              {this.state.products.map(product => <tr key={product.id}>
                <td><img className="img-cart" src={product.thumbnail} alt={product.id}></img></td>
                <td>{product.name}{product.attributes.brand}</td>
                <td>{product.price}</td>
                <td>{product.quantity}</td>
                <td>{ (Math.floor(product.price * product.quantity * 100) / 100)}</td>
              </tr>)}
            </tbody>
          </table>
        </div>
      </div>
      <div className="price">
        <div className="price-wrapper">
          <div className="total-price">
            <p>Total to pay</p>
            <p className="price-number">€500.00</p>
          </div>
          <button className="checkout-btn">Proceed to checkout</button>
        </div>
      </div>
    </div>;
  }
}
