import * as actionTypes from "./actionTypes";

export const addProductOrder = product => {
  return {
    type: actionTypes.ADD_PRODUCT_ORDER,
    product: product
  };
};

export const removeProductOrder = productId => {
  return {
    type: actionTypes.REMOVE_PRODUCT_ORDER,
    productId: productId
  };
};

export const fetchProductOrder = () => {
  return {
    type: actionTypes.FETCH_PRODUCT_ORDER
  };
};
