import React from "react";
import "./UserMenu.css";
import { Link } from "react-router-dom";

export default function UserMenu(props) {
  return (
    <div className="UserMenu" onClick={() => props.closeFullscreen()}>
      <p className="greeting">Hello, </p>
      {true ? (
        <React.Fragment>
          <Link to={"/secure/products"}>Manage Products</Link>
          <Link to={"/secure/users"}>Manage Users</Link>
          <Link to={"/secure/orders"}>Manage Orders</Link>
        </React.Fragment>
      ) : null}
      <Link to={"/orders"}>My Orders</Link>
      <Link to={"/user"}>Settings</Link>
      <div className="logout">Log Out</div>
    </div>
  );
}
