import React from "react";
import "./ProductsHome.css";

export default function ProductsHome(props) {
  return (
    <div className="ProductsHome">
      <img src={props.link} alt="" />
      <div className="info-product-home">
        <h2>{props.title}</h2>
        <p>{props.description}</p>
      </div>
    </div>
  );
}
